{% comment %} Newsletter signup section - Basic {% endcomment %}
<section class="newsletter-section" role="region" aria-labelledby="newsletter-heading">
  <div class="newsletter-container page-width">
    {% if section.settings.heading != blank %}
      <h2 id="newsletter-heading" class="newsletter-heading">{{ section.settings.heading | t }}</h2>
    {% endif %}

    {% if section.settings.description != blank %}
      <p class="newsletter-description">{{ section.settings.description | t }}</p>
    {% endif %}

    {% form 'customer', class: 'newsletter-form' %}
      <div class="newsletter-field-group">
        <label for="NewsletterEmail" class="visually-hidden">{{ 'sections.newsletter.email_label' | t }}</label>
        <input
          type="email"
          id="NewsletterEmail"
          name="contact[email]"
          placeholder="{{ 'sections.newsletter.email_placeholder' | t }}"
          aria-describedby="newsletter-privacy-note"
          required
          autocorrect="off"
          autocapitalize="off"
        >
        <button
          type="submit"
          class="newsletter-button"
          aria-label="{{ 'sections.newsletter.submit_button_label' | t }}"
        >
          {{ 'sections.newsletter.submit_button' | t }}
        </button>
      </div>

      {% if section.settings.enable_privacy %}
        <p id="newsletter-privacy-note" class="newsletter-privacy">
          {{ 'sections.newsletter.privacy_text' | t }}
          {% if routes.privacy_policy != blank %}
            <a href="{{ routes.privacy_policy_url }}" target="_blank" rel="noopener noreferrer">{{ 'sections.newsletter.privacy_link' | t }}</a>
          {% else %}
            {{ 'sections.newsletter.privacy_link' | t }}
          {% endif %}
        </p>
      {% endif %}
    {% endform %}
  </div>
</section>

{% schema %}
{
  "name": "{{ 'sections.newsletter.name' | t }}",
  "tag": "section",
  "class": "spaced-section",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "{{ 'sections.newsletter.heading_label' | t }}",
      "default": "{{ 'sections.newsletter.heading_default' | t }}"
    },
    {
      "type": "richtext",
      "id": "description",
      "label": "{{ 'sections.newsletter.description_label' | t }}",
      "default": "<p>{{ 'sections.newsletter.description_default' | t }}</p>"
    },
    {
      "type": "checkbox",
      "id": "enable_privacy",
      "label": "{{ 'sections.newsletter.privacy_checkbox_label' | t }}",
      "default": true
    }
  ],
  "presets": [
    {
      "name": "{{ 'sections.newsletter.preset_name' | t }}",
      "category": "{{ 'sections.newsletter.category' | t }}"
    }
  ],
  "enabled_on": {
    "pages": ["home"]
  }
}
{% endschema %}

{% comment %} Hero banner section {% endcomment %}
<section class="hero-banner" role="region" aria-label="{{ 'sections.hero.aria_label' | t }}">
  {% if section.settings.image != blank %}
    <div class="hero-image" style="background-image: url('{{ section.settings.image | img_url: '3840x' }}'); background-position: {{ section.settings.image.focal_point | default: '50% 50%' }}; background-size: cover; background-repeat: no-repeat;" loading="lazy">
  {% else %}
    <div class="hero-image placeholder">
  {% endif %}
    <div class="hero-content page-width">
      {% if section.settings.title != blank %}
        <h1 class="hero-title">{{ section.settings.title | t }}</h1>
      {% endif %}
      {% if section.settings.subtitle != blank %}
        <p class="hero-subtitle">{{ section.settings.subtitle | t }}</p>
      {% endif %}
      {% if section.settings.button_label != blank and section.settings.button_link != blank %}
        <a href="{{ section.settings.button_link }}" class="hero-button" aria-label="{{ section.settings.button_label | t }}">
          {{ section.settings.button_label | t }}
        </a>
      {% endif %}
    </div>
  </div>
</section>

