{% comment %} Product recommendations by tag {% endcomment %}
{% assign current_product_tags = product.tags %}
{% assign recommendations = collections.all.products | where: 'tags', current_product_tags.first %}
{% assign final_recommendations = '' | split: ',' %}

{% for prod in recommendations %}
  {% unless prod.handle == product.handle %}
    {% for tag in current_product_tags %}
      {% if prod.tags contains tag %}
        {% unless final_recommendations contains prod %}
          {% assign final_recommendations = final_recommendations | push: prod %}
        {% endunless %}
        {% break %}
      {% endif %}
    {% endfor %}
  {% endunless %}
{% endfor %}

{% assign rec_count = final_recommendations.size %}
{% if rec_count >= 2 %}
<section class="product-recommendations" role="region" aria-labelledby="recommendations-heading">
  <div class="page-width">
    <h2 id="recommendations-heading" class="recommendations-heading">{{ section.settings.heading | t }}</h2>
    <ul class="product-grid grid grid--2-col-tablet grid--4-col-desktop">
      {% for item in final_recommendations limit: section.settings.limit %}
        <li class="product-grid__item">
          <a href="{{ item.url }}" class="product-card">
            {% if item.featured_image != blank %}
              <img src="{{ item.featured_image | img_url: '300x' }}" alt="{{ item.title | escape }}" loading="lazy">
            {% else %}
              <div class="product-card__placeholder"></div>
            {% endif %}
            <h3 class="product-card__title">{{ item.title }}</h3>
            <div class="price">
              {% if item.compare_at_price > item.price %}
                <span class="price--compare">{{ item.compare_at_price | money }}</span>
                <span class="price--sale">{{ item.price | money }}</span>
              {% else %}
                <span>{{ item.price | money }}</span>
              {% endif %}
            </div>
          </a>
          <form action="/cart/add" method="post" enctype="multipart/form-data" class="quick-buy-form">
            <input type="hidden" name="id" value="{{ item.variants.first.id }}">
            <button type="submit" class="button button--secondary" aria-label="{{ 'products.product.add_to_cart' | t: product: item.title }}">
              {{ 'products.product.add_to_cart' | t }}
            </button>
          </form>
        </li>
      {% endfor %}
    </ul>
  </div>
</section>
{% endif %}

{% schema %}
{
  "name": "{{ 'sections.recommendations.name' | t }}",
  "class": "spaced-section",
