<div class="hero{% if section.settings.text_alignment %} hero--{{ section.settings.text_alignment }}{% endif %}">
  {% if section.settings.hero_image != blank %}
    <div class="hero__media">
      <img src="{{ section.settings.hero_image | image_url: width: 1920 }}"
           srcset="{{ section.settings.hero_image | image_url: width: 750 }} 750w,
                   {{ section.settings.hero_image | image_url: width: 1296 }} 1296w,
                   {{ section.settings.hero_image | image_url: width: 1920 }} 1920w"
           sizes="100vw"
           alt="{{ section.settings.hero_image.alt | escape }}"
           loading="lazy"
           width="{{ section.settings.hero_image.width }}"
           height="{{ section.settings.hero_image.height }}">
    </div>
  {% endif %}

  <div class="hero__content">
    {% if section.settings.heading != blank %}
      <h1 class="hero__heading">{{ section.settings.heading | escape }}</h1>
    {% endif %}

    {% if section.settings.subheading != blank %}
      <p class="hero__subheading">{{ section.settings.subheading | escape }}</p>
    {% endif %}

    {% if section.settings.cta_text != blank and section.settings.cta_link != blank %}
      <a href="{{ section.settings.cta_link }}" 
         class="hero__cta button button--primary"
         aria-label="{{ section.settings.cta_text | escape }}">
        {{ section.settings.cta_text | escape }}
      </a>
    {% endif %}
  </div>
</div>

<style>
.hero {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 60vh;
  overflow: hidden;
}

.hero__media {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
}

.hero__media img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.hero__content {
  text-align: center;
  padding: 2rem;
  max-width: 800px;
}

.hero--left .hero__content { text-align: left; }
.hero--right .hero__content { text-align: right; }

.hero__heading {
  font-size: clamp(2rem, 5vw, 4rem);
  margin-bottom: 1rem;
  color: #fff;
}

.hero__subheading {
  font-size: clamp(1rem, 3vw, 1.5rem);
  margin-bottom: 2rem;
  color: #fff;
  opacity: 0.9;
}

.hero__cta {
