{% comment %} Basic Slideshow - No JS {% endcomment %}
<section class="slideshow-section" role="region" aria-label="{{ 'sections.slideshow.aria_label' | t }}">
  <div class="slideshow-wrapper">
    {% for block in section.blocks limit: 3 %}
      <div class="slide" id="slide-{{ block.id }}" aria-label="{{ 'sections.slideshow.slide' | t: number: forloop.index }}">
        {% if block.settings.image != blank %}
          <img src="{{ block.settings.image | img_url: '2048x' }}" alt="{{ block.settings.heading | escape }}" loading="{% if forloop.first %}eager{% else %}lazy{% endif %}" width="2048" height="800">
        {% else %}
          <div class="slide-placeholder" aria-hidden="true"></div>
        {% endif %}
        <div class="slide-content">
          {% if block.settings.heading != blank %}
            <h2 class="slide-heading">{{ block.settings.heading | t }}</h2>
          {% endif %}
          {% if block.settings.subheading != blank %}
            <p class="slide-subheading">{{ block.settings.subheading | t }}</p>
          {% endif %}
          {% if block.settings.button_label != blank and block.settings.button_link != blank %}
            <a href="{{ block.settings.button_link }}" class="slide-button" aria-label="{{ block.settings.button_label | t }}">{{ block.settings.button_label | t }}</a>
          {% endif %}
        </div>
      </div>
    {% endfor %}

    {% if section.blocks.size > 1 %}
      <div class="slideshow-dots">
        {% for block in section.blocks %}
          <button class="dot" aria-label="{{ 'sections.slideshow.go_to_slide' | t: number: forloop.index }}" data-slide="{{ forloop.index }}"></button>
        {% endfor %}
      </div>
    {% endif %}
  </div>
</section>

