    {%- when 'search' -%}
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
    {%- when 'menu' -%}
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="3" y1="12" x2="21" y2="12"></line><line x1="3" y1="6" x2="21" y2="6"></line><line x1="3" y1="18" x2="21" y2="18"></line></svg>
    {%- when 'close' -%}
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg>
    {%- when 'arrow-right' -%}
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"><line x1="5" y1="12" x2="19" y2="12"></line><polyline points="12 5 19 12 12 19"></polyline></svg>
    {%- else -%}
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"><circle cx="12" cy="12" r="10"></circle></svg>
  {%- endcase -%}
{%- endcapture -%}

<span class="icon icon--{{ icon_name }} {{ icon_class }}" style="width: {{ icon_size }}px; height: {{ icon_size }}px;" {% if rtl_flip %}dir="rtl"{% endif %}>
  {% if is_decorative %}
    {{ icon_svg | replace: '<svg', '<svg aria-hidden="true"' }}
  {% else %}
    <span class="visually-hidden">{{ aria_label | default: icon_name }}</span>
    {{ icon_svg }}
