    <ul class="pagination__list">
      {% if paginate.previous %}
        <li class="pagination__item">
          <a href="{{ paginate.previous.url }}" class="pagination__link" aria-label="{{ 'general.pagination.previous' | t }}">
            {{ 'general.pagination.previous' | t }}
