      {% else %}
        <li class="pagination__item pagination__item--disabled" aria-hidden="true">
          {{ 'general.pagination.previous' | t }}
        </li>
      {% endif %}

      {% for part in paginate.parts %}
        {% if part.is_link %}
          <li class="pagination__item">
            <a href="{{ part.url }}" class="pagination__link">{{ part.title }}</a>
          </li>
        {% elsif part.title == paginate.current_page %}
          <li class="pagination__item" aria-current="page">
            <span class="pagination__current">{{ part.title }}</span>
          </li>
        {% else %}
          <li class="pagination__item">
            <span class="pagination__gap">{{ part.title }}</span>
          </li>
        {% endif %}
      {% endfor %}

      {% if paginate.next %}
        <li class="pagination__item">
          <a href="{{ paginate.next.url }}" class="pagination__link" aria-label="{{ 'general.pagination.next' | t }}">
            {{ 'general.pagination.next' | t }}
          </a>
        </li>
      {% else %}
        <li class="pagination__item pagination__item--disabled" aria-hidden="true">
          {{ 'general.pagination.next' | t }}
        </li>
      {% endif %}
    </ul>
  </nav>
{% endif %}

