  </nav>

  <script type="application/ld+json">
  {
    "@context": "https://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": [
      {% for crumb in crumbs %}
        {
          "@type": "ListItem",
          "position": {{ forloop.index }},
