{% comment %} Advanced Product Template {% endcomment %}
<div class="product-template" data-section-id="{{ section.id }}" data-section-type="product">
  <div class="product-layout grid grid--1-col-tablet grid--2-col-desktop">
    <div class="product-media-container">
      {% if product.media.size > 0 %}
        <div class="product-gallery">
          <div class="product-main-media">
            {% for media in product.media %}
              <div class="media-item {% if forloop.first %}active{% endif %}" data-index="{{ forloop.index0 }}">
                {% if media.media_type == 'image' %}
                  <img src="{{ media | img_url: '800x' }}" alt="{{ media.alt | default: product.title }}" loading="{% if forloop.first %}eager{% else %}lazy{% endif %}" width="800" height="800">
                {% elsif media.media_type == 'video' %}
                  {{ media | media_tag: image_size: '800x' }}
                {% elsif media.media_type == 'model' %}
                  {{ media | media_tag: image_size: '800x' }}
                {% endif %}
              </div>
            {% endfor %}
          </div>
          {% if product.media.size > 1 %}
            <div class="product-thumbnails">
              {% for media in product.media %}
                <button class="thumbnail" aria-label="{{ 'products.product.media_thumbnail' | t: index: forloop.index }}" data-index="{{ forloop.index0 }}">
                  <img src="{{ media | img_url: '100x' }}" alt="" loading="lazy" width="100" height="100">
                </button>
              {% endfor %}
            </div>
          {% endif %}
        </div>
      {% endif %}
    </div>

    <div class="product-info">
      <h1 class="product-title">{{ product.title }}</h1>

      {% if product.vendor != blank %}
        <p class="product-vendor">{{ product.vendor }}</p>
      {% endif %}

      <div class="price" id="price-{{ section.id }}">
        {% include 'price', product: product %}
      </div>

      {% form 'product', product, class: 'product-form' %}
        {% if product.has_only_default_variant == false %}
          <div class="product-variants">
            {% for option in product.options_with_values %}
              <div class="variant-option">
                <label for="option-{{ section.id }}-{{ forloop.index0 }}">{{ option.name }}</label>
                <select id="option-{{ section.id }}-{{ forloop.index0 }}" name="{{ option.name | handleize }}">
                  {% for value in option.values %}
                    <option value="{{ value | escape }}" {% if option.selected_value == value %}selected{% endif %}>
                      {{ value }}
                    </option>
                  {% endfor %}
                </select>
              </div>
            {% endfor %}
          </div>
        {% endif %}

        <div class="product-quantity">
          <label for="quantity-{{ section.id }}">{{ 'products.product.quantity.label' | t }}</label>
          <input type="number" id="quantity-{{ section.id }}" name="quantity" value="1" min="1" max="999" aria-label="{{ 'products.product.quantity.label' | t }}">
        </div>

        <div class="product-buttons">
          <button type="submit" name="add" class="button" {% if product.available == false %}disabled{% endif %}>
            {% if product.available == false %}
              {{ 'products.product.sold_out' | t }}
            {% else %}
              {{ 'products.product.add_to_cart' | t }}
            {% endif %}
          </button>

          {{ form | payment_button }}
        </div>
      {% endform %}

      <div class="product-accordion">
        <details open>
          <summary>{{ 'products.product.description.title' | t }}</summary>
          <div class="rte">{{ product.description }}</div>
        </details>
        <details>
          <summary>{{ 'products.product.shipping.title' | t }}</summary>
          <div class="rte">{{ pages.shipping.content }}</div>
        </details>
        <details>
          <summary>{{ 'products.product.returns.title' | t }}</summary>
          <div class="rte">{{ pages.returns.content }}</div>
        </details>
      </div>
    </div>
  </div>

  {% if recommendations.performed %}
    <section class="product-recommendations-section">
      <h2>{{ 'products.recommendations.heading' | t }}</h2>
      {% for product in recommendations.products %}
        {% include 'product-card', product: product %}
      {% endfor %}
    </section>
  {% endif %}
</div>

