{% comment %}
Slideshow Hero Section
This section displays a rotating or static set of slides with images, text, and CTAs.
{% endcomment %}

{% style %}
  .slideshow-hero {
    position: relative;
    overflow: hidden;
    width: 100%;
  }

  .slideshow-container {
    position: relative;
    width: 100%;
    height: 60vh; /* Default height */
    min-height: 400px; /* Minimum height */
  }

  .slide {
    display: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }

  .slide.active {
    display: block;
  }

  .slide-content {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 2;
    text-align: center;
    color: #ffffff; /* Default text color for overlay */
    max-width: 80%;
  }

  .slide-image,
  .slide-video {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 1;
  }

  .slide-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.3); /* Default dark overlay */
    z-index: 1;
  }

  .slideshow-nav {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 3;
    display: flex;
    gap: 10px;
  }

  .slideshow-dot {
    background-color: #bbb;
    border: none;
    border-radius: 50%;
    cursor: pointer;
    opacity: 0.5;
    transition: opacity 0.6s ease;
    width: 15px;
    height: 15px;
  }

  .slideshow-dot.active,
  .slideshow-dot:hover {
    opacity: 1;
  }

  .slideshow-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background-color: rgba(0, 0, 0, 0.5);
    color: white;
    font-size: 18px;
    border: none;
    cursor: pointer;
    padding: 16px;
    z-index: 3;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .slideshow-arrow:hover {
    background-color: rgba(0, 0, 0, 0.8);
  }

  .prev {
    left: 15px;
  }

  .next {
    right: 15px;
  }

  .cta-button {
    display: inline-block;
    margin-top: 15px;
    padding: 12px 24px;
    text-decoration: none;
    border-radius: 4px;
    font-weight: bold;
    cursor: pointer;
    border: none;
    transition: background-color 0.3s ease;
  }

  .cta-primary {
    background-color: #000000;
    color: #ffffff;
  }

  .cta-primary:hover {
    background-color: #333333;
  }

  .cta-secondary {
    background-color: transparent;
    color: #ffffff;
    border: 2px solid #ffffff;
    margin-left: 10px;
  }

  .cta-secondary:hover {
    background-color: rgba(255, 255, 255, 0.1);
  }

  @media screen and (max-width: 768px) {
    .slideshow-container {
      height: 50vh;
      min-height: 300px;
    }

    .slide-content {
      max-width: 95%;
    }

    .slideshow-arrow {
      width: 40px;
      height: 40px;
      font-size: 16px;
      padding: 12px;
    }

    .prev {
      left: 10px;
    }

    .next {
      right: 10px;
    }
  }
{% endstyle %}

<div class="slideshow-hero" id="Slideshow-{{ section.id }}">
  <div class="slideshow-container" role="region" aria-label="{{ 'sections.slideshow.carousel' | t }}" aria-live="polite">
    {% for block in section.blocks %}
      {% if block.type == 'slide' %}
        <div class="slide {% if forloop.first %}active{% endif %}" id="slide-{{ section.id }}-{{ forloop.index }}" aria-hidden="{% unless forloop.first %}true{% else %}false{% endunless %}" role="group" aria-roledescription="slide" aria-label="{{ forloop.index }} {{ 'sections.slideshow.of' | t }} {{ section.blocks.size }}">
          {% comment %} Slide Background {% endcomment %}
          {% if block.settings.slide_image != blank %}
            <img
