              <a href="{{ block.settings.slide_button_link }}" class="cta-button cta-primary">{{ block.settings.slide_button_label | escape }}</a>
            {% endif %}
            {% if block.settings.slide_second_button_label != blank and block.settings.slide_second_button_link != blank %}
              <a href="{{ block.settings.slide_second_button_link }}" class="cta-button cta-secondary">{{ block.settings.slide_second_button_label | escape }}</a>
            {% endif %}
          </div>
        </div>
      {% endif %}
    {% endfor %}

    {% if section.blocks.size > 1 %}
      <button class="slideshow-arrow prev" aria-label="{{ 'sections.slideshow.previous_slide' | t }}">&#10094;</button>
      <button class="slideshow-arrow next" aria-label="{{ 'sections.slideshow.next_slide' | t }}">&#10095;</button>

      <div class="slideshow-nav" role="tablist" aria-label="{{ 'sections.slideshow.slide_navigation' | t }}">
        {% for block in section.blocks %}
          {% if block.type == 'slide' %}
            <button class="slideshow-dot {% if forloop.first %}active{% endif %}" aria-label="{{ 'sections.slideshow.go_to_slide' | t: index: forloop.index }}" aria-selected="{% if forloop.first %}true{% else %}false{% endif %}" data-slide-index="{{ forloop.index0 }}"></button>
          {% endif %}
        {% endfor %}
      </div>
    {% endif %}
  </div>
</div>

{% javascript %}
  // Slideshow Logic
  document.addEventListener('DOMContentLoaded', function() {
    const slideshowId = 'Slideshow-{{ section.id }}';
    const container = document.querySelector(`#${slideshowId} .slideshow-container`);
    const slides = container.querySelectorAll('.slide');
    const dots = container.querySelectorAll('.slideshow-dot');
    const prevBtn = container.querySelector('.prev');
    const nextBtn = container.querySelector('.next');
    const autoRotate = {{ section.settings.auto_rotate }};
    const rotationSpeed = {{ section.settings.rotation_speed | times: 1000 }}; // Convert seconds to milliseconds

    let currentIndex = 0;
    let rotationInterval;

    function showSlide(index) {
      // Hide all slides and remove active class from dots
      slides.forEach(slide => slide.classList.remove('active'));
      dots.forEach(dot => dot.classList.remove('active')); 

      // Update ARIA attributes
      slides.forEach((slide, i) => {
        slide.setAttribute('aria-hidden', i !== index);
      });
      dots.forEach((dot, i) => {
        dot.setAttribute('aria-selected', i === index);
      });

      // Show selected slide and mark dot as active
      slides[index].classList.add('active');
      dots[index].classList.add('active');
      currentIndex = index;
    }

    function nextSlide() {
      const nextIndex = (currentIndex + 1) % slides.length;
      showSlide(nextIndex);
    }

    function startRotation() {
      if (autoRotate && slides.length > 1) {
        rotationInterval = setInterval(nextSlide, rotationSpeed);
      }
    }

    function stopRotation() {
      clearInterval(rotationInterval);
    }

    // Event listeners for manual controls
    prevBtn?.addEventListener('click', () => {
      stopRotation();
      const prevIndex = (currentIndex - 1 + slides.length) % slides.length;
      showSlide(prevIndex);
      startRotation(); // Resume after interaction
    });

    nextBtn?.addEventListener('click', () => {
      stopRotation();
      nextSlide();
      startRotation(); // Resume after interaction
    });

    dots.forEach((dot, index) => {
      dot.addEventListener('click', () => {
        stopRotation();
        showSlide(index);
        startRotation(); // Resume after interaction
      });
    });

    // Pause/resume on hover/focus
    container.addEventListener('mouseenter', stopRotation);
    container.addEventListener('mouseleave', startRotation);
    container.addEventListener('focusin', stopRotation); // For focus management
    container.addEventListener('focusout', startRotation);

    // Keyboard navigation for dots
    dots.forEach((dot, index) => {
      dot.addEventListener('keydown', (e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          stopRotation();
          showSlide(index);
          startRotation();
          dot.focus(); // Maintain focus
        }
      });
    });

    // Initialize
    startRotation();
  });
{% endjavascript %}

