    background-color: #ff6b6b;
  }

  .product-card__info {
    padding: 16px;
    display: flex;
    flex-direction: column;
    gap: 8px;
  }

  .product-card__title {
    margin: 0;
    font-size: 16px;
    line-height: 1.4;
  }

  .product-card__title a {
    text-decoration: none;
    color: inherit;
  }

  .product-card__vendor {
    margin: 0;
    font-size: 14px;
    color: #666;
  }

  .product-card__price {
    display: flex;
    gap: 8px;
    align-items: center;
  }

  .product-card__price--current {
    font-weight: bold;
    color: #000;
  }

  .product-card__price--original {
    text-decoration: line-through;
    color: #999;
    font-size: 14px;
  }

  .product-card__rating {
    display: flex;
    align-items: center;
    gap: 4px;
    font-size: 14px;
  }

  .icon {
    width: 16px;
    height: 16px;
  }

  .icon--star-filled {
    fill: #ffc107;
    stroke: #ffc107;
  }

  .icon--star-empty {
    stroke: #ccc;
  }

  .product-card__add-form {
    margin-top: 8px;
  }

  .product-card__add-button {
    width: 100%;
    padding: 10px;
    background-color: #000;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-weight: bold;
    transition: background-color 0.3s ease;
  }

  .product-card__add-button:hover {
    background-color: #333;
  }

  @media (max-width: 768px) {
    .product-card {
      max-width: 280px;
    }
  }
