{% comment %}
Responsive Navigation Menu
A flexible navigation system supporting desktop dropdowns and mobile toggle.
{% endcomment %}

<nav class="main-navigation" aria-label="{{ 'general.header.main_navigation' | t }}">
  <div class="navigation-container">
    <div class="nav-logo">
      <a href="{{ routes.root_url }}" aria-label="{{ 'general.header.logo_aria_label' | t }}">
        {% if section.settings.logo != blank %}
          {{ section.settings.logo | img_url: '200x' | img_tag: alt: shop.name, class: 'logo-image' }}
        {% else %}
          <span class="site-name">{{ shop.name }}</span>
        {% endif %}
      </a>
    </div>

    <div class="nav-menu-toggle">
      <button 
        class="menu-toggle"
        aria-expanded="false"
        aria-controls="main-menu"
        aria-label="{{ 'general.header.menu_toggle_label' | t }}"
      >
        <span class="menu-toggle-icon"></span>
        <span class="menu-toggle-icon"></span>
        <span class="menu-toggle-icon"></span>
      </button>
    </div>

    <ul id="main-menu" class="nav-menu" role="menubar">
      {% for link in linklists[section.settings.main_menu].links %}
        <li class="nav-menu-item" role="none">
          {% if link.links != empty %}
            <a 
              href="{{ link.url }}" 
              class="nav-menu-link has-dropdown" 
              aria-haspopup="true" 
              aria-expanded="false" 
              role="menuitem"
              tabindex="0"
            >
              {{ link.title }}
              <span class="dropdown-indicator" aria-hidden="true">▼</span>
            </a>
            <ul class="dropdown-menu" role="menu" aria-label="{{ link.title }} submenu">
              {% for childlink in link.links %}
                <li class="dropdown-item" role="none">
                  {% if childlink.links != empty %}
                    <a 
                      href="{{ childlink.url }}" 
                      class="dropdown-link has-sub-dropdown" 
                      aria-haspopup="true" 
                      aria-expanded="false" 
                      role="menuitem"
                      tabindex="0"
                    >
                      {{ childlink.title }}
                      <span class="sub-dropdown-indicator" aria-hidden="true">▶</span>
                    </a>
                    <ul class="sub-dropdown-menu" role="menu" aria-label="{{ childlink.title }} submenu">
                      {% for grandchildlink in childlink.links %}
                        <li class="sub-dropdown-item" role="none">
                          <a href="{{ grandchildlink.url }}" class="sub-dropdown-link" role="menuitem" tabindex="0">
                            {{ grandchildlink.title }}
                          </a>
                        </li>
                      {% endfor %}
                    </ul>
                  {% else %}
                    <a href="{{ childlink.url }}" class="dropdown-link" role="menuitem" tabindex="0">{{ childlink.title }}</a>
                  {% endif %}
                </li>
              {% endfor %}
            </ul>
          {% else %}
            <a href="{{ link.url }}" class="nav-menu-link" role="menuitem" tabindex="0">{{ link.title }}</a>
          {% endif %}
        </li>
      {% endfor %}

      <li class="nav-icons" role="none">
        <a href="{{ routes.search_url }}" class="nav-icon" aria-label="{{ 'general.header.search' | t }}" role="menuitem" tabindex="0">
          <svg class="icon-search" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <circle cx="11" cy="11" r="8" />
            <path d="m21 21-4.35-4.35" />
          </svg>
        </a>
        <a href="{{ routes.account_url }}" class="nav-icon" aria-label="{{ 'general.header.account' | t }}" role="menuitem" tabindex="0">
          <svg class="icon-account" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" />
            <circle cx="12" cy="7" r="4" />
          </svg>
        </a>
        <a href="{{ routes.cart_url }}" class="nav-icon" aria-label="{{ 'general.header.cart' | t }}" role="menuitem" tabindex="0">
          <svg class="icon-cart" viewBox="0 0 24 24" fill="none" stroke="currentColor">
            <circle cx="9" cy="21" r="1" />
            <circle cx="20" cy="21" r="1" />
            <path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6" />
          </svg>
          {% if cart.item_count > 0 %}
            <span class="cart-count">{{ cart.item_count }}</span>
          {% endif %}
        </a>
      </li>
    </ul>
  </div>
</nav>

{% javascript %}
  document.addEventListener('DOMContentLoaded', function() {
    // Mobile menu toggle
    const menuToggle = document.querySelector('.menu-toggle');
    const mainMenu = document.getElementById('main-menu');

    menuToggle.addEventListener('click', function() {
      const expanded = this.getAttribute('aria-expanded') === 'true';
      this.setAttribute('aria-expanded', !expanded);
      mainMenu.classList.toggle('mobile-open');
    });

    // Desktop dropdown handling
    const dropdownTriggers = document.querySelectorAll('.has-dropdown');

    dropdownTriggers.forEach(trigger => {
      // Mouse events
      trigger.addEventListener('mouseenter', function() {
        openDropdown(this);
      });

      trigger.parentElement.addEventListener('mouseleave', function() {
        closeDropdown(this.querySelector('.has-dropdown'));
      });

      // Keyboard events
      trigger.addEventListener('keydown', function(e) {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          toggleDropdown(this);
        } else if (e.key === 'Escape') {
          closeDropdown(this);
        }
      });
    });

    // Sub-dropdown handling
    const subDropdownTriggers = document.querySelectorAll('.has-sub-dropdown');

    subDropdownTriggers.forEach(trigger => {
      trigger.addEventListener('mouseenter', function() {
        openSubDropdown(this);
      });

      trigger.parentElement.addEventListener('mouseleave', function() {
        closeSubDropdown(this.querySelector('.has-sub-dropdown'));
      });

      trigger.addEventListener('keydown', function(e) {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          toggleSubDropdown(this);
        } else if (e.key === 'Escape') {
          closeSubDropdown(this);
        }
      });
    });

    function toggleDropdown(element) {
      const isOpen = element.getAttribute('aria-expanded') === 'true';
      if (isOpen) {
        closeDropdown(element);
      } else {
        openDropdown(element);
      }
    }

    function openDropdown(element) {
      element.setAttribute('aria-expanded', 'true');
      element.nextElementSibling.classList.add('open');
    }

    function closeDropdown(element) {
      element.setAttribute('aria-expanded', 'false');
      element.nextElementSibling.classList.remove('open');

      // Close any open sub-menus
      const openSubMenus = element.parentElement.querySelectorAll('.sub-dropdown-menu.open');
      openSubMenus.forEach(menu => {
        menu.classList.remove('open');
        menu.previousElementSibling.setAttribute('aria-expanded', 'false');
      });
    }

    function toggleSubDropdown(element) {
      const isOpen = element.getAttribute('aria-expanded') === 'true';
      if (isOpen) {
        closeSubDropdown(element);
      } else {
        openSubDropdown(element);
      }
    }

    function openSubDropdown(element) {
      element.setAttribute('aria-expanded', 'true');
      element.nextElementSibling.classList.add('open');
    }

    function closeSubDropdown(element) {
      element.setAttribute('aria-expanded', 'false');
      element.nextElementSibling.classList.remove('open');
    }

    // Close menus when clicking outside
    document.addEventListener('click', function(event) {
      if (!event.target.closest('.nav-menu-item')) {
        dropdownTriggers.forEach(trigger => {
          if (trigger.getAttribute('aria-expanded') === 'true') {
            closeDropdown(trigger);
          }
        });

        subDropdownTriggers.forEach(trigger => {
          if (trigger.getAttribute('aria-expanded') === 'true') {
            closeSubDropdown(trigger);
          }
        });
      }
    });
  });
{% endjavascript %}

