          "default": "Happy Customer"
        },
        {
          "type": "image_picker",
          "id": "avatar",
          "label": "Author avatar"
        }
      ]
    }
  ],
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "Section heading",
      "default": "What Our Customers Say"
    },
    {
      "type": "range",
      "id": "autoplay_speed",
      "label": "Autoplay speed (seconds)",
      "min": 3,
      "max": 10,
      "step": 1,
      "default": 5
    }
  ],
  "presets": [
    {
      "name": "Testimonials",
      "blocks": [
        {
          "type": "testimonial"
        },
        {
          "type": "testimonial"
        },
        {
          "type": "testimonial"
        }
      ]
    }
  ]
}
{% endschema %}

<div class="testimonials">
  <div class="testimonials__container">
    {% if section.settings.heading != blank %}
      <h2 class="testimonials__heading">{{ section.settings.heading | escape }}</h2>
    {% endif %}

    {% if section.blocks.size > 0 %}
      <div class="testimonials__carousel" data-testimonial-carousel>
        <div class="testimonials__slides" data-testimonial-slides>
          {% for block in section.blocks %}
            <div class="testimonials__slide{% unless forloop.first %} hidden{% endunless %}" data-testimonial-slide>
              <div class="testimonials__card">
                <div class="testimonials__content">
                  <p class="testimonials__text">{{ block.settings.text | escape }}</p>
                </div>

                <div class="testimonials__author">
                  {% if block.settings.avatar != blank %}
                    <img src="{{ block.settings.avatar | image_url: width: 80 }}"
                         alt="{{ block.settings.author | escape }}"
                         class="testimonials__avatar"
                         width="80"
                         height="80">
                  {% endif %}

                  <div class="testimonials__author-info">
                    <p class="testimonials__author-name">{{ block.settings.author | escape }}</p>
                    {% if block.settings.role != blank %}
                      <p class="testimonials__author-role">{{ block.settings.role | escape }}</p>
                    {% endif %}
                  </div>
                </div>
              </div>
            </div>
          {% endfor %}
        </div>

        {% if section.blocks.size > 1 %}
          <div class="testimonials__navigation" data-testimonial-nav>
            {% for block in section.blocks %}
              <button class="testimonials__dot{% if forloop.first %} testimonials__dot--active{% endif %}"
                      data-testimonial-dot="{{ forloop.index0 }}"
                      aria-label="View testimonial {{ forloop.index }}">
              </button>
            {% endfor %}
          </div>
        {% endif %}
      </div>
    {% endif %}
  </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', function() {
  const carousel = document.querySelector('[data-testimonial-carousel]');
  if (!carousel) return;

  const slides = carousel.querySelectorAll('[data-testimonial-slide]');
  const dots = carousel.querySelectorAll('[data-testimonial-dot]');
  const autoplaySpeed = {{ section.settings.autoplay_speed | default: 5 }} * 1000;

  let currentSlide = 0;
  let autoplayTimer;
  let isPaused = false;

  function showSlide(index) {
    slides.forEach((slide, i) => {
      slide.classList.toggle('hidden', i !== index);
    });

    dots.forEach((dot, i) => {
      dot.classList.toggle('testimonials__dot--active', i === index);
    });

    currentSlide = index;
  }

  function nextSlide() {
    const nextIndex = (currentSlide + 1) % slides.length;
    showSlide(nextIndex);
  }

  function startAutoplay() {
    if (slides.length <= 1) return;
    autoplayTimer = setInterval(() => {
      if (!isPaused) nextSlide();
    }, autoplaySpeed);
  }

  function stopAutoplay() {
    clearInterval(autoplayTimer);
  }

  // Event listeners
  dots.forEach((dot, index) => {
    dot.addEventListener('click', () => {
      stopAutoplay();
      showSlide(index);
      startAutoplay();
    });
  });

  carousel.addEventListener('mouseenter', () => {
    isPaused = true;
  });

  carousel.addEventListener('mouseleave', () => {
    isPaused = false;
  });

  // Keyboard navigation
  carousel.addEventListener('keydown', (e) => {
    if (e.key === 'ArrowLeft') {
      const prevIndex = (currentSlide - 1 + slides.length) % slides.length;
      showSlide(prevIndex);
    } else if (e.key === 'ArrowRight') {
      nextSlide();
    }
  });

  // Start autoplay
  startAutoplay();
});
</script>

