{% comment %}
Slideshow Hero Section
This section displays a rotating or static set of slides with images, text, and CTAs.
{% endcomment %}

{% style %}
  .slideshow-hero {
    position: relative;
    overflow: hidden;
    width: 100%;
  }

  .slideshow-container {
    position: relative;
    width: 100%;
    height: 60vh; /* Default height */
    min-height: 400px; /* Minimum height */
  }

  .slide {
    display: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }

  .slide.active {
    display: block;
  }

  .slide-content {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 2;
    text-align: center;
    color: #ffffff; /* Default text color for overlay */
    max-width: 80%;
  }

  .slide-image,
  .slide-video {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 1;
  }

  .slide-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.3); /* Default dark overlay */
    z-index: 1;
  }

  .slideshow-nav {
    position: absolute;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 3;
    display: flex;
    gap: 10px;
  }

  .slideshow-dot {
    background-color: #bbb;
    border: none;
    border-radius: 50%;
    cursor: pointer;
    opacity: 0.5;
    transition: opacity 0.6s ease;
    width: 15px;
    height: 15px;
  }

  .slideshow-dot.active,
  .slideshow-dot:hover {
    opacity: 1;
  }

  .slideshow-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background-color: rgba(0, 0, 0, 0.5);
    color: white;
    font-size: 18px;
    border: none;
    cursor: pointer;
    padding: 16px;
    z-index: 3;
    border-radius: 50%;
    width: 50px;
    height: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .slideshow-arrow:hover {
    background-color: rgba(0, 0, 0, 0.8);
  }

  .prev {
    left: 15px;
  }

  .next {
    right: 15px;
  }

  .cta-button {
    display: inline-block;
    margin-top: 15px;
    padding: 12px 24px;
    text-decoration: none;
    border-radius: 4px;
    font-weight: bold;
    cursor: pointer;
    border: none;
    transition: background-color 0.3s ease;
  }

  .cta-primary {
    background-color: #000000;
    color: #ffffff;
  }

  .cta-primary:hover {
    background-color: #333333;
  }

  .cta-secondary {
    background-color: transparent;
    color: #ffffff;
    border: 2px solid #ffffff;
    margin-left: 10px;
  }

  .cta-secondary:hover {
    background-color: rgba(255, 255, 255, 0.1);
  }

  @media screen and (max-width: 768px) {
    .slideshow-container {
      height: 50vh;
      min-height: 300px;
    }

    .slide-content {
      max-width: 95%;
    }

    .slideshow-arrow {
      width: 40px;
      height: 40px;
      font-size: 16px;
      padding: 12px;
    }

    .prev {
      left: 10px;
    }

    .next {
      right: 10px;
    }
  }
{% endstyle %}

<div class="slideshow-hero" id="Slideshow-{{ section.id }}">
  <div class="slideshow-container" role="region" aria-label="{{ 'sections.slideshow.carousel' | t }}" aria-live="polite">
    {% for block in section.blocks %}
      {% if block.type == 'slide' %}
        <div class="slide {% if forloop.first %}active{% endif %}" id="slide-{{ section.id }}-{{ forloop.index }}" aria-hidden="{% unless forloop.first %}true{% else %}false{% endunless %}" role="group" aria-roledescription="slide" aria-label="{{ forloop.index }} {{ 'sections.slideshow.of' | t }} {{ section.blocks.size }}">
          {% comment %} Slide Background {% endcomment %}
          {% if block.settings.slide_image != blank %}
            <img
              src="{{ block.settings.slide_image | img_url: '1600x' }}"
              srcset="{{ block.settings.slide_image | img_url: '800x' }} 800w,
                      {{ block.settings.slide_image | img_url: '1200x' }} 1200w,
                      {{ block.settings.slide_image | img_url: '1600x' }} 1600w"
              sizes="100vw"
              alt="{% if block.settings.slide_image.alt != blank %}{{ block.settings.slide_image.alt }}{% else %}{{ 'sections.slideshow.image_alt' | t: index: forloop.index }}{% endif %}"
              class="slide-image"
              loading="lazy"
            >
          {% elsif block.settings.slide_video != blank %}
            <video
              autoplay
              muted
              loop
              playsinline
              class="slide-video"
              poster="{{ block.settings.slide_video.preview_image_url | img_url: '1600x' }}"
            >
              <source src="{{ block.settings.slide_video | escape }}" type="video/mp4">
              {% comment %} Fallback image if video fails {% endcomment %}
              <img
                src="{{ block.settings.slide_video.preview_image_url | img_url: '1600x' }}"
                alt="{% if block.settings.slide_video.preview_image_url.alt != blank %}{{ block.settings.slide_video.preview_image_url.alt }}{% else %}{{ 'sections.slideshow.video_fallback_alt' | t: index: forloop.index }}{% endif %}"
                class="slide-image"
              >
            </video>
          {% else %}
            {% comment %} Placeholder if no image/video {% endcomment %}
            <div class="placeholder-background"></div>
          {% endif %}

          <div class="slide-overlay"></div>

          <div class="slide-content">
            {% if block.settings.slide_headline != blank %}
              <h2 class="slide-headline">{{ block.settings.slide_headline | escape }}</h2>
            {% endif %}
            {% if block.settings.slide_subheading != blank %}
              <p class="slide-subheading">{{ block.settings.slide_subheading | escape }}</p>
            {% endif %}
            {% if block.settings.slide_button_label != blank and block.settings.slide_button_link != blank %}
              <a href="{{ block.settings.slide_button_link }}" class="cta-button cta-primary">{{ block.settings.slide_button_label | escape }}</a>
            {% endif %}
            {% if block.settings.slide_second_button_label != blank and block.settings.slide_second_button_link != blank %}
              <a href="{{ block.settings.slide_second_button_link }}" class="cta-button cta-secondary">{{ block.settings.slide_second_button_label | escape }}</a>
            {% endif %}
          </div>
        </div>
      {% endif %}
    {% endfor %}

    {% if section.blocks.size > 1 %}
      <button class="slideshow-arrow prev" aria-label="{{ 'sections.slideshow.previous_slide' | t }}">&#10094;</button>
      <button class="slideshow-arrow next" aria-label="{{ 'sections.slideshow.next_slide' | t }}">&#10095;</button>

      <div class="slideshow-nav" role="tablist" aria-label="{{ 'sections.slideshow.slide_navigation' | t }}">
        {% for block in section.blocks %}
          {% if block.type == 'slide' %}
            <button class="slideshow-dot {% if forloop.first %}active{% endif %}" aria-label="{{ 'sections.slideshow.go_to_slide' | t: index: forloop.index }}" aria-selected="{% if forloop.first %}true{% else %}false{% endif %}" data-slide-index="{{ forloop.index0 }}"></button>
          {% endif %}
        {% endfor %}
      </div>
    {% endif %}
  </div>
</div>

{% javascript %}
  // Slideshow Logic
  document.addEventListener('DOMContentLoaded', function() {
    const slideshowId = 'Slideshow-{{ section.id }}';
    const container = document.querySelector(`#${slideshowId} .slideshow-container`);
    const slides = container.querySelectorAll('.slide');
    const dots = container.querySelectorAll('.slideshow-dot');
    const prevBtn = container.querySelector('.prev');
    const nextBtn = container.querySelector('.next');
    const autoRotate = {{ section.settings.auto_rotate }};
    const rotationSpeed = {{ section.settings.rotation_speed | times: 1000 }}; // Convert seconds to milliseconds

    let currentIndex = 0;
    let rotationInterval;

    function showSlide(index) {
      // Hide all slides and remove active class from dots
      slides.forEach(slide => slide.classList.remove('active'));
      dots.forEach(dot => dot.classList.remove('active')); 

      // Update ARIA attributes
      slides.forEach((slide, i) => {
        slide.setAttribute('aria-hidden', i !== index);
      });
      dots.forEach((dot, i) => {
        dot.setAttribute('aria-selected', i === index);
      });

      // Show selected slide and mark dot as active
      slides[index].classList.add('active');
      dots[index].classList.add('active');
      currentIndex = index;
    }

    function nextSlide() {
      const nextIndex = (currentIndex + 1) % slides.length;
      showSlide(nextIndex);
    }

    function startRotation() {
      if (autoRotate && slides.length > 1) {
        rotationInterval = setInterval(nextSlide, rotationSpeed);
      }
    }

    function stopRotation() {
      clearInterval(rotationInterval);
    }

    // Event listeners for manual controls
    prevBtn?.addEventListener('click', () => {
      stopRotation();
      const prevIndex = (currentIndex - 1 + slides.length) % slides.length;
      showSlide(prevIndex);
      startRotation(); // Resume after interaction
    });

    nextBtn?.addEventListener('click', () => {
      stopRotation();
      nextSlide();
      startRotation(); // Resume after interaction
    });

    dots.forEach((dot, index) => {
      dot.addEventListener('click', () => {
        stopRotation();
        showSlide(index);
        startRotation(); // Resume after interaction
      });
    });

    // Pause/resume on hover/focus
    container.addEventListener('mouseenter', stopRotation);
    container.addEventListener('mouseleave', startRotation);
    container.addEventListener('focusin', stopRotation); // For focus management
    container.addEventListener('focusout', startRotation);

    // Keyboard navigation for dots
    dots.forEach((dot, index) => {
      dot.addEventListener('keydown', (e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          stopRotation();
          showSlide(index);
          startRotation();
          dot.focus(); // Maintain focus
        }
      });
    });

    // Initialize
    startRotation();
  });
{% endjavascript %}

{% schema %}
  {
    "name": "{{ 'sections.slideshow.name' | t }}",
    "tag": "section",
    "class": "spaced-section slideshow-hero-section",
