      {
        "type": "range",
        "id": "rotation_speed",
        "min": 3,
        "max": 10,
        "step": 1,
        "unit": "{{ 'sections.slideshow.rotation_speed.unit' | t }}",
        "default": 5,
        "label": "{{ 'sections.slideshow.rotation_speed.label' | t }}",
        "info": "{{ 'sections.slideshow.rotation_speed.info' | t }}"
