          },
          {
            "type": "text",
            "id": "slide_second_button_label",
            "default": "{{ 'sections.slideshow.blocks.slide.settings.slide_second_button_label.default' | t }}",
            "label": "{{ 'sections.slideshow.blocks.slide.settings.slide_second_button_label.label' | t }}"
          },
          {
            "type": "url",
            "id": "slide_second_button_link",
            "label": "{{ 'sections.slideshow.blocks.slide.settings.slide_second_button_link.label' | t }}"
          }
        ]
      }
    ],
    "presets": [
      {
        "name": "{{ 'sections.slideshow.presets.name' | t }}",
        "blocks": [
          {
            "type": "slide"
          },
          {
            "type": "slide"
          }
        ]
      }
    ],
    "enabled_on": {
      "templates": [
        "index"
      ]
    }
  }
{% endschema %}

