Product Card Component
Displays key product information in a compact card format.
{% endcomment %}

<article class="product-card" aria-labelledby="product-title-{{ product.id }}">
  <div class="product-card__image-wrapper">
    <a href="{{ product.url | default: '#' }}" class="product-card__image-link" aria-label="{{ 'products.product.link_to_product' | t: title: product.title }}">
      <img 
        src="{{ product.featured_media | img_url: '400x' }}"
        srcset="{{ product.featured_media | img_url: '200x' }} 200w,
                {{ product.featured_media | img_url: '400x' }} 400w,
                {{ product.featured_media | img_url: '600x' }} 600w"
        sizes="(min-width: 990px) 33vw, (min-width: 750px) 50vw, 100vw"
        alt="{{ product.featured_media.alt | escape }}"
        loading="lazy"
        class="product-card__image"
      >
    </a>
    {% if product.compare_at_price_max > product.price %}
      <span class="product-card__badge product-card__badge--sale">{{ 'products.product.on_sale' | t }}</span>
    {% endif %}
  </div>

  <div class="product-card__info">
    <h3 class="product-card__title" id="product-title-{{ product.id }}">
      <a href="{{ product.url | default: '#' }}">{{ product.title | escape }}</a>
    </h3>

    {% if product.vendor %}
      <p class="product-card__vendor">{{ product.vendor }}</p>
    {% endif %}

    <div class="product-card__price">
      {% if product.compare_at_price_max > product.price %}
        <span class="product-card__price--current" aria-label="{{ 'products.product.currently_on_sale_from_html' | t: price: product.price | escape }}">
          {{ product.price | money }}
        </span>
        <span class="product-card__price--original" aria-label="{{ 'products.product.regular_price' | t }}">
          {{ product.compare_at_price_max | money }}
        </span>
      {% else %}
        <span class="product-card__price--current">
          {{ product.price | money }}
        </span>
      {% endif %}
    </div>

    {% if product.metafields.reviews.rating.value %}
      <div class="product-card__rating" aria-label="{{ 'products.product.reviews.average_rating_of_count' | t: rating_value: product.metafields.reviews.rating.value, review_count: product.metafields.reviews.count.value }}">
        <span class="product-card__stars">
          {% for i in (1..5) %}
            {% if i <= product.metafields.reviews.rating.value %}
              <svg class="icon icon--star-filled" viewBox="0 0 16 16"><path d="M8 1L10.39 6.5H16l-4.4 3.2 1.64 5.06L8 11.28 2.76 14.74 4.4 9.7 0 6.5h5.61z"/></svg>
            {% else %}
              <svg class="icon icon--star-empty" viewBox="0 0 16 16"><path d="M8 1L10.39 6.5H16l-4.4 3.2 1.64 5.06L8 11.28 2.76 14.74 4.4 9.7 0 6.5h5.61z" stroke-width="1" fill="none"/></svg>
            {% endif %}
          {% endfor %}
        </span>
        <span class="product-card__review-count">({{ product.metafields.reviews.count.value }})</span>
      </div>
    {% endif %}

    <div class="product-card__actions">
      <form action="/cart/add" method="post" enctype="multipart/form-data" class="product-card__add-form">
        <input type="hidden" name="id" value="{{ product.selected_or_first_available_variant.id }}" />
        <button type="submit" class="product-card__add-button" aria-label="{{ 'products.product.add_to_cart' | t }}">
          {{ 'products.product.add_to_cart' | t }}
        </button>
      </form>
    </div>
  </div>
</article>

