{% comment %}
Customer Account Dashboard
Central hub for managing customer account information and preferences.
{% endcomment %}

{% if customer %}
<div class="account-dashboard">
  <div class="dashboard-header">
    <h1>{{ 'customer.account.title' | t }}</h1>
    <p>{{ 'customer.account.welcome' | t: first_name: customer.first_name }}</p>
  </div>

  <div class="dashboard-grid">
    <!-- Profile Section -->
    <section class="dashboard-section" aria-labelledby="profile-heading">
      <h2 id="profile-heading">{{ 'customer.account.profile' | t }}</h2>
      <div class="profile-info">
        <p><strong>{{ 'customer.account.name' | t }}:</strong> {{ customer.name }}</p>
        <p><strong>{{ 'customer.account.email' | t }}:</strong> {{ customer.email }}</p>
        <p><strong>{{ 'customer.account.accepts_marketing' | t }}:</strong> 
          {% if customer.accepts_marketing %}
            {{ 'customer.account.yes' | t }}
          {% else %}
            {{ 'customer.account.no' | t }}
          {% endif %}
        </p>
      </div>
      <button type="button" class="btn-edit" data-modal-target="edit-profile-modal">{{ 'customer.account.edit_profile' | t }}</button>
    </section>

    <!-- Loyalty Points Section -->
    {% if customer.metafields.loyalty.points %}
    <section class="dashboard-section" aria-labelledby="loyalty-heading">
      <h2 id="loyalty-heading">{{ 'customer.account.loyalty_points' | t }}</h2>
      <div class="loyalty-info">
        <p class="points-balance">{{ customer.metafields.loyalty.points.value }} {{ 'customer.account.points' | t }}</p>
        <p class="points-description">{{ 'customer.account.loyalty_description' | t }}</p>
      </div>
    </section>
    {% endif %}

    <!-- Recent Orders Section -->
    <section class="dashboard-section" aria-labelledby="orders-heading">
      <h2 id="orders-heading">{{ 'customer.account.recent_orders' | t }}</h2>
      <div class="orders-list">
        {% for order in customer.orders limit: 5 %}
        <div class="order-item">
          <div class="order-summary">
            <span class="order-number">#{{ order.name }}</span>
            <span class="order-date">{{ order.created_at | date: format: 'date' }}</span>
            <span class="order-status status-{{ order.status }}">{{ order.status_label }}</span>
            <span class="order-total">{{ order.total_price | money }}</span>
          </div>
          <div class="order-details">
            <a href="{{ order.customer_url }}" class="btn-view-order">{{ 'customer.account.view_order' | t }}</a>
            {% if order.fulfillment_status == 'fulfilled' and order.tracking_number %}
            <a href="{{ order.shipping_tracking_url }}" target="_blank" class="btn-track-order">{{ 'customer.account.track_shipment' | t }}</a>
            {% endif %}
          </div>
        </div>
        {% endfor %}
      </div>
      <a href="{{ routes.account_orders_url }}" class="btn-view-all">{{ 'customer.account.view_all_orders' | t }}</a>
    </section>

    <!-- Addresses Section -->
    <section class="dashboard-section" aria-labelledby="addresses-heading">
      <h2 id="addresses-heading">{{ 'customer.account.addresses' | t }}</h2>
      <div class="addresses-list">
        {% for address in customer.addresses %}
        <div class="address-item">
          <p>{{ address.first_name }} {{ address.last_name }}</p>
          <p>{{ address.company }}</p>
          <p>{{ address.address1 }}</p>
          <p>{{ address.address2 }}</p>
          <p>{{ address.city }}, {{ address.province }} {{ address.zip }}</p>
          <p>{{ address.country }}</p>
          <p>{{ address.phone }}</p>
          <div class="address-actions">
            <button type="button" class="btn-edit" data-address-id="{{ address.id }}">{{ 'customer.account.edit_address' | t }}</button>
            <button type="button" class="btn-delete" data-address-id="{{ address.id }}">{{ 'customer.account.delete_address' | t }}</button>
          </div>
        </div>
        {% endfor %}
      </div>
      <button type="button" class="btn-add-address" data-modal-target="add-address-modal">{{ 'customer.account.add_address' | t }}</button>
    </section>

    <!-- Wish List Section -->
    {% if customer.metafields.wishlist.items.size > 0 %}
    <section class="dashboard-section" aria-labelledby="wishlist-heading">
      <h2 id="wishlist-heading">{{ 'customer.account.wishlist' | t }}</h2>
      <div class="wishlist-products">
        {% for item in customer.metafields.wishlist.items.value %}
        <div class="wishlist-item">
          <a href="{{ item.url }}">
            <img src="{{ item.image | img_url: '100x' }}" alt="{{ item.title }}">
            <span class="wishlist-product-title">{{ item.title }}</span>
          </a>
          <span class="wishlist-product-price">{{ item.price | money }}</span>
          <button class="btn-remove-wishlist" data-product-id="{{ item.id }}">{{ 'customer.account.remove_from_wishlist' | t }}</button>
        </div>
        {% endfor %}
      </div>
    </section>
    {% endif %}
  </div>

  <!-- Edit Profile Modal -->
  <div id="edit-profile-modal" class="modal" aria-hidden="true">
    <div class="modal-content">
      <h3>{{ 'customer.account.edit_profile' | t }}</h3>
      <form id="edit-customer-form" action="{{ routes.account_edit_url }}" method="post">
        <div class="form-group">
          <label for="first_name">{{ 'customer.account.first_name' | t }}</label>
          <input type="text" id="first_name" name="customer[first_name]" value="{{ customer.first_name }}" required>
        </div>
        <div class="form-group">
          <label for="last_name">{{ 'customer.account.last_name' | t }}</label>
          <input type="text" id="last_name" name="customer[last_name]" value="{{ customer.last_name }}" required>
        </div>
        <div class="form-group">
          <label for="email">{{ 'customer.account.email' | t }}</label>
          <input type="email" id="email" name="customer[email]" value="{{ customer.email }}" required>
        </div>
        <div class="form-group">
          <label>
            <input type="checkbox" name="customer[accepts_marketing]" value="1" {% if customer.accepts_marketing %}checked{% endif %}>
            {{ 'customer.account.accepts_marketing' | t }}
          </label>
        </div>
        <div class="form-actions">
          <button type="button" class="btn-cancel" data-modal-close>Cancel</button>
          <button type="submit" class="btn-save">{{ 'customer.account.save_changes' | t }}</button>
        </div>
      </form>
    </div>
  </div>

  <!-- Add Address Modal -->
  <div id="add-address-modal" class="modal" aria-hidden="true">
    <div class="modal-content">
      <h3>{{ 'customer.account.add_address' | t }}</h3>
      <form id="add-address-form" action="{{ routes.account_addresses_url }}" method="post">
        <div class="form-row">
          <div class="form-group">
            <label for="address_first_name">{{ 'customer.addresses.first_name' | t }}</label>
            <input type="text" id="address_first_name" name="address[first_name]" required>
          </div>
          <div class="form-group">
            <label for="address_last_name">{{ 'customer.addresses.last_name' | t }}</label>
            <input type="text" id="address_last_name" name="address[last_name]" required>
          </div>
        </div>
        <div class="form-group">
          <label for="address_company">{{ 'customer.addresses.company' | t }}</label>
          <input type="text" id="address_company" name="address[company]">
        </div>
        <div class="form-group">
          <label for="address_address1">{{ 'customer.addresses.address1' | t }}</label>
          <input type="text" id="address_address1" name="address[address1]" required>
        </div>
        <div class="form-group">
          <label for="address_address2">{{ 'customer.addresses.address2' | t }}</label>
          <input type="text" id="address_address2" name="address[address2]">
        </div>
        <div class="form-row">
          <div class="form-group">
            <label for="address_city">{{ 'customer.addresses.city' | t }}</label>
            <input type="text" id="address_city" name="address[city]" required>
          </div>
          <div class="form-group">
            <label for="address_province">{{ 'customer.addresses.province' | t }}</label>
            <select id="address_province" name="address[province]" required>
              {% for province in cart.shipping_provinces %}
                <option value="{{ province }}">{{ province }}</option>
              {% endfor %}
            </select>
          </div>
          <div class="form-group">
            <label for="address_zip">{{ 'customer.addresses.zip' | t }}</label>
            <input type="text" id="address_zip" name="address[zip]" required>
          </div>
        </div>
        <div class="form-group">
          <label for="address_country">{{ 'customer.addresses.country' | t }}</label>
          <select id="address_country" name="address[country]" required>
            {% for country in Localization.available_countries %}
              <option value="{{ country.iso_code }}" {% if country.iso_code == cart.shipping_country %}selected{% endif %}>
                {{ country.name }}
              </option>
            {% endfor %}
          </select>
        </div>
        <div class="form-group">
          <label for="address_phone">{{ 'customer.addresses.phone' | t }}</label>
          <input type="tel" id="address_phone" name="address[phone]">
        </div>
        <div class="form-actions">
          <button type="button" class="btn-cancel" data-modal-close>Cancel</button>
          <button type="submit" class="btn-save">{{ 'customer.account.add_address' | t }}</button>
        </div>
      </form>
    </div>
  </div>
</div>

{% javascript %}
  document.addEventListener('DOMContentLoaded', function() {
    // Modal functionality
    const modals = document.querySelectorAll('.modal');
    const openModalButtons = document.querySelectorAll('[data-modal-target]');
    const closeModalButtons = document.querySelectorAll('[data-modal-close]');

    openModalButtons.forEach(button => {
      button.addEventListener('click', () => {
        const modalId = button.getAttribute('data-modal-target');
        const modal = document.getElementById(modalId);
        modal.setAttribute('aria-hidden', 'false');
        modal.style.display = 'flex';

        // Focus first input in modal
        const firstInput = modal.querySelector('input, select, textarea, button:not(.btn-cancel)');
        if (firstInput) firstInput.focus();
      });
    });

    closeModalButtons.forEach(button => {
      button.addEventListener('click', () => {
        const modal = button.closest('.modal');
        modal.setAttribute('aria-hidden', 'true');
        modal.style.display = 'none';
      });
    });

    window.addEventListener('click', (event) => {
      modals.forEach(modal => {
        if (event.target === modal) {
          modal.setAttribute('aria-hidden', 'true');
          modal.style.display = 'none';
        }
      });
    });

    // Address deletion confirmation
    const deleteAddressButtons = document.querySelectorAll('.btn-delete');
    deleteAddressButtons.forEach(button => {
      button.addEventListener('click', function() {
        const addressId = this.getAttribute('data-address-id');
        if (confirm('{{ 'customer.account.confirm_delete_address' | t }}')) {
          fetch(`/account/addresses/${addressId}`, {
            method: 'DELETE',
            headers: {
              'X-Requested-With': 'XMLHttpRequest',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              authenticity_token: '{{ form._form_id }}'
            })
          }).then(response => {
            if (response.ok) {
              location.reload();
            }
          });
        }
      });
    });

    // Form submission handling
    const editCustomerForm = document.getElementById('edit-customer-form');
    if (editCustomerForm) {
      editCustomerForm.addEventListener('submit', function(e) {
        e.preventDefault();

        const formData = new FormData(this);
        fetch(this.action, {
          method: 'PUT',
          body: formData
        }).then(response => {
          if (response.ok) {
            document.getElementById('edit-profile-modal').setAttribute('aria-hidden', 'true');
            document.getElementById('edit-profile-modal').style.display = 'none';
            location.reload();
          }
        });
      });
    }
  });
{% endjavascript %}

