{% comment %}
Dynamic FAQ Accordion Section
Allows merchants to create collapsible Q&A content with search and filtering.
{% endcomment %}

<section class="faq-section" aria-labelledby="faq-title">
  <div class="container">
    <h2 id="faq-title">{{ section.settings.title }}</h2>

    {% if section.settings.show_search %}
    <div class="faq-search">
      <input 
        type="text" 
        id="faq-search-input" 
        placeholder="{{ 'sections.faq.search_placeholder' | t }}" 
        aria-label="{{ 'sections.faq.search_label' | t }}"
      >
    </div>
    {% endif %}

    {% if section.settings.show_category_filter %}
    <div class="faq-filters">
      <select id="faq-category-filter" aria-label="{{ 'sections.faq.category_filter_label' | t }}">
        <option value="all">{{ 'sections.faq.all_categories' | t }}</option>
        {% assign categories = "" | split: ',' %}
        {% for block in section.blocks %}
          {% if block.settings.category and block.settings.category != blank %}
            {% unless categories contains block.settings.category %}
              {% assign categories = categories | push: block.settings.category %}
            {% endunless %}
          {% endif %}
        {% endfor %}
        {% for category in categories %}
          <option value="{{ category | handle }}">{{ category }}</option>
        {% endfor %}
      </select>
    </div>
    {% endif %}

    <div class="faq-accordion" id="faq-accordion">
      {% for block in section.blocks %}
        {% if block.type == 'faq_item' %}
        <div 
          class="faq-item" 
          data-category="{{ block.settings.category | handle }}" 
          data-search="{{ block.settings.question | downcase }} {{ block.settings.answer | strip_html | downcase }}"
        >
          <button 
            class="faq-question" 
            aria-expanded="false" 
            aria-controls="faq-answer-{{ block.id }}"
            id="faq-question-{{ block.id }}"
          >
            <span class="faq-question-text">{{ block.settings.question }}</span>
            <span class="faq-icon" aria-hidden="true">+</span>
          </button>
          <div 
            class="faq-answer" 
            id="faq-answer-{{ block.id }}" 
            role="region" 
            aria-labelledby="faq-question-{{ block.id }}" 
            hidden
          >
            <div class="faq-answer-content">{{ block.settings.answer }}</div>
          </div>
        </div>
        {% endif %}
      {% endfor %}
    </div>

    {% if section.settings.show_expand_all %}
    <div class="faq-actions">
      <button id="expand-all-btn" class="faq-action-btn">{{ 'sections.faq.expand_all' | t }}</button>
      <button id="collapse-all-btn" class="faq-action-btn">{{ 'sections.faq.collapse_all' | t }}</button>
    </div>
    {% endif %}
  </div>
</section>

<script type="application/ld+json">
{
  "@context": "https://schema.org",
  "@type": "FAQPage",
  "mainEntity": [
    {% for block in section.blocks %}
      {% if block.type == 'faq_item' %}
      {
        "@type": "Question",
        "name": "{{ block.settings.question | escape }}",
        "acceptedAnswer": {
          "@type": "Answer",
          "text": "{{ block.settings.answer | strip_html | escape }}"
        }
      }{% unless forloop.last %},{% endunless %}
      {% endif %}
    {% endfor %}
  ]
}
</script>

{% javascript %}
  document.addEventListener('DOMContentLoaded', function() {
    const faqItems = document.querySelectorAll('.faq-item');
    const searchInput = document.getElementById('faq-search-input');
    const categoryFilter = document.getElementById('faq-category-filter');
    const expandAllBtn = document.getElementById('expand-all-btn');
    const collapseAllBtn = document.getElementById('collapse-all-btn');

    // Toggle individual FAQ
    faqItems.forEach(item => {
      const questionBtn = item.querySelector('.faq-question');

      questionBtn.addEventListener('click', function() {
        const answer = this.nextElementSibling;
        const isExpanded = this.getAttribute('aria-expanded') === 'true';

        // Close all other FAQs if single_expanded is enabled
        if ({{ section.settings.single_expanded | json }}) {
          faqItems.forEach(otherItem => {
            if (otherItem !== item) {
              const otherQuestion = otherItem.querySelector('.faq-question');
              const otherAnswer = otherItem.querySelector('.faq-answer');
              otherQuestion.setAttribute('aria-expanded', 'false');
              otherAnswer.hidden = true;
              otherQuestion.querySelector('.faq-icon').textContent = '+';
            }
          });
        }

        // Toggle current FAQ
        this.setAttribute('aria-expanded', !isExpanded);
        answer.hidden = isExpanded;
        this.querySelector('.faq-icon').textContent = isExpanded ? '+' : '-';

        // Focus the answer content for screen readers
        if (!isExpanded) {
          answer.focus();
        }
      });

      // Keyboard navigation
      questionBtn.addEventListener('keydown', function(e) {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          this.click();
        }
      });
    });

    // Search functionality
    if (searchInput) {
      searchInput.addEventListener('input', function() {
        const searchTerm = this.value.toLowerCase().trim();

        faqItems.forEach(item => {
          const searchData = item.dataset.search;
          const matches = searchData.includes(searchTerm);

          if (matches) {
            item.style.display = 'block';
          } else {
            item.style.display = 'none';
          }
        });
      });
    }

    // Category filtering
    if (categoryFilter) {
      categoryFilter.addEventListener('change', function() {
        const selectedCategory = this.value;

        faqItems.forEach(item => {
          const itemCategory = item.dataset.category;

          if (selectedCategory === 'all' || itemCategory === selectedCategory) {
            item.style.display = 'block';
          } else {
            item.style.display = 'none';
          }
        });
      });
    }

    // Expand/Collapse all
    if (expandAllBtn && collapseAllBtn) {
      expandAllBtn.addEventListener('click', function() {
        faqItems.forEach(item => {
          const question = item.querySelector('.faq-question');
          const answer = item.querySelector('.faq-answer');

          question.setAttribute('aria-expanded', 'true');
          answer.hidden = false;
          question.querySelector('.faq-icon').textContent = '-';
        });
      });

      collapseAllBtn.addEventListener('click', function() {
        faqItems.forEach(item => {
          const question = item.querySelector('.faq-question');
          const answer = item.querySelector('.faq-answer');

          question.setAttribute('aria-expanded', 'false');
          answer.hidden = true;
          question.querySelector('.faq-icon').textContent = '+';
        });
      });
    }
  });
{% endjavascript %}

