{% if section.settings.show_announcement %}
  <div class="announcement"
       data-announcement
       data-countdown-date="{{ section.settings.countdown_date }}"
       data-countdown-time="{{ section.settings.countdown_time }}"
       role="banner">
    <div class="announcement__content">
      {% if section.settings.link != blank %}
        <a href="{{ section.settings.link }}" class="announcement__link">
      {% endif %}

      <span class="announcement__message">{{ section.settings.message | escape }}</span>

      {% if section.settings.countdown_date != blank %}
        <span class="announcement__countdown" data-countdown aria-live="polite">
          <span class="announcement__countdown-label">Ends in:</span>
          <span class="announcement__countdown-time" data-countdown-display></span>
        </span>
      {% endif %}

      {% if section.settings.link != blank %}
        </a>
      {% endif %}
    </div>

    <button class="announcement__close" data-announcement-close aria-label="Close announcement">
      <span class="announcement__close-icon" aria-hidden="true">×</span>
    </button>
  </div>
{% endif %}

<script>
document.addEventListener('DOMContentLoaded', function() {
  const announcement = document.querySelector('[data-announcement]');
  if (!announcement) return;

  const closeButton = announcement.querySelector('[data-announcement-close]');
  const countdownDisplay = announcement.querySelector('[data-countdown-display]');
  const countdownDate = announcement.dataset.countdownDate;
  const countdownTime = announcement.dataset.countdownTime;

  // Check if user has dismissed
  const dismissed = localStorage.getItem('announcement-dismissed');
  if (dismissed && new Date(dismissed) > new Date()) {
    announcement.style.display = 'none';
    return;
  }

  // Countdown functionality
  if (countdownDate && countdownDisplay) {
    const endDate = new Date(countdownDate + 'T' + countdownTime);

    function updateCountdown() {
      const now = new Date();
      const diff = endDate - now;

      if (diff <= 0) {
        announcement.style.display = 'none';
        return;
      }

      const days = Math.floor(diff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((diff % (1000 * 60)) / 1000);

      let timeString = '';
      if (days > 0) timeString += days + 'd ';
      if (hours > 0 || days > 0) timeString += hours + 'h ';
      if (minutes > 0 || hours > 0 || days > 0) timeString += minutes + 'm ';
      timeString += seconds + 's';

      countdownDisplay.textContent = timeString;
    }

    updateCountdown();
    setInterval(updateCountdown, 1000);
  }

  // Close functionality
  closeButton.addEventListener('click', function() {
    announcement.style.display = 'none';
    // Store dismissal for 7 days
    const expiry = new Date();
    expiry.setDate(expiry.getDate() + 7);
    localStorage.setItem('announcement-dismissed', expiry.toISOString());
  });
});
</script>

