  .announcement {
    padding: 0.75rem;
    font-size: 0.8125rem;
  }

  .announcement__content {
    gap: 0.5rem;
  }

  .announcement__countdown {
    font-size: 0.75rem;
    padding: 0.25rem 0.5rem;
  }

  .announcement__close {
    right: 0.5rem;
  }
}
</style>

{% if section.blocks.size > 0 %}
<section class="slideshow" aria-label="{{ 'sections.slideshow.carousel_label' | t }}">
  <div class="slideshow__track">
    {% for block in section.blocks %}
      <div class="slideshow__slide" {{ block.shopify_attributes }}>
        {% if block.settings.image != blank %}
          {%- assign img_url = block.settings.image | image_url: width: 2048 -%}
          <img srcset="{{ img_url }}&width=400 400w,
                        {{ img_url }}&width=800 800w,
                        {{ img_url }}&width=1200 1200w,
                        {{ img_url }}&width=1600 1600w,
                        {{ img_url }}&width=2048 2048w"
               src="{{ img_url }}&width=1200"
               alt="{{ block.settings.image.alt | escape }}"
               loading="{% if forloop.first %}eager{% else %}lazy{% endif %}"
               width="{{ block.settings.image.width }}"
               height="{{ block.settings.image.height }}"
               class="slideshow__image">
        {% elsif block.settings.video_url != blank %}
          <video autoplay muted loop playsinline aria-hidden="true" class="slideshow__video">
            <source src="{{ block.settings.video_url }}" type="video/mp4">
          </video>
        {% else %}
          <div class="slideshow__placeholder" aria-hidden="true"></div>
        {% endif %}

        <div class="slideshow__content">
          {% if block.settings.title != blank %}
            <h2 class="slideshow__title">{{ block.settings.title | t }}</h2>
          {% endif %}
          {% if block.settings.subtitle != blank %}
            <p class="slideshow__subtitle">{{ block.settings.subtitle | t }}</p>
          {% endif %}
          {% if block.settings.button_label != blank and block.settings.button_link != blank %}
            <a href="{{ block.settings.button_link }}" class="slideshow__button" aria-label="{{ block.settings.button_label | t }}">
              {{ block.settings.button_label | t }}
            </a>
          {% endif %}
        </div>
      </div>
    {% endfor %}
  </div>

  {% if section.blocks.size > 1 %}
    <button class="slideshow__arrow slideshow__arrow--prev" aria-label="{{ 'sections.slideshow.previous_slide' | t }}">‹</button>
    <button class="slideshow__arrow slideshow__arrow--next" aria-label="{{ 'sections.slideshow.next_slide' | t }}">›</button>
    <div class="slideshow__dots" role="tablist" aria-label="{{ 'sections.slideshow.slide_selection' | t }}">
      {% for i in (1..section.blocks.size) %}
        <button class="slideshow__dot" role="tab" aria-selected="{% if forloop.first %}true{% else %}false{% endif %}" aria-label="{{ 'sections.slideshow.go_to_slide' | t: number: i }}">{{ i }}</button>
      {% endfor %}
    </div>
  {% endif %}
</section>
{% else %}
  <div class="slideshow__empty">{{ 'sections.slideshow.empty' | t }}</div>
{% endif %}

{% schema %}
{
  "name": "{{'sections.slideshow.name' | t}}",
  "tag": "section",
  "class": "slideshow-section",
  "max_blocks": 5,
  "blocks": [
    {
      "type": "slide",
      "name": "{{'sections.slideshow.slide_name' | t}}",
      "settings": [
