{% comment %} Usage: {% include 'collection-card', collection: collection %} {% endcomment %}

{%- assign collection_image = collection.image | default: collection.products.first.featured_image -%}

<a href="{{ collection.url }}" class="collection-card" aria-label="{{ collection.title | escape }}{% if settings.show_product_count %}, {{ 'sections.collection_card.product_count' | t: count: collection.all_products_count }}{% endif %}">
  <div class="collection-card__image-wrapper">
    {% if collection_image != blank %}
      {%- assign img_url = collection_image | image_url: width: 600 -%}
      <img srcset="{{ img_url }}&width=300 300w,
                    {{ img_url }}&width=600 600w"
           src="{{ img_url }}&width=600"
           alt="{{ collection_image.alt | escape }}"
           loading="lazy"
           width="600"
           height="600"
           class="collection-card__image">
    {% else %}
      <div class="collection-card__placeholder" aria-hidden="true"></div>
    {% endif %}
  </div>

  <div class="collection-card__content">
    <h3 class="collection-card__title">{{ collection.title | truncate: 40 }}</h3>
    {% if settings.show_product_count and collection.all_products_count > 0 %}
      <span class="collection-card__count">{{ 'sections.collection_card.product_count' | t: count: collection.all_products_count }}</span>
    {% endif %}
  </div>
</a>

{% comment %} Usage: {% include 'price-display', product: product %} {% endcomment %}

{%- assign product = include.product -%}

<div class="price-display" role="text" aria-label="{{ 'products.product.price_aria_label' | t }}">
  {% if product.price_varies %}
    {% if product.price_min == 0 %}
      <span class="price price--free">{{ 'products.product.free' | t }}</span>
    {% else %}
      <span class="price">{{ product.price_min | money }}</span>
      <span class="price--range">– {{ product.price_max | money }}</span>
    {% endif %}
  {% else %}
    {% if product.compare_at_price > product.price and product.available %}
      <span class="price">{{ product.price | money }}</span>
      <span class="price--compare">{{ product.compare_at_price | money }}</span>
      <span class="badge badge--sale">{{ 'products.product.on_sale' | t }}</span>
    {% elsif product.price == 0 %}
      <span class="price price--free">{{ 'products.product.free' | t }}</span>
    {% else %}
      <span class="price">{{ product.price | money }}</span>
    {% endif %}
  {% endif %}
</div>

{% layout 'search' %}

<header class="search-header">
  <h1>{{ 'templates.search.results_for_html' | t: terms: search.terms }}</h1>
  <p class="search-result-count" role="status">{{ 'templates.search.results_count' | t: count: search.results_count }}</p>
</header>

<div class="search-tabs" role="tablist" aria-label="{{ 'templates.search.tabs_label' | t }}">
  <button id="tab-all" class="search-tab active" role="tab" aria-selected="true" aria-controls="panel-all">{{ 'templates.search.all' | t }}</button>
  <button id="tab-products" class="search-tab" role="tab" aria-selected="false" aria-controls="panel-products">{{ 'templates.search.products' | t }}</button>
  <button id="tab-articles" class="search-tab" role="tab" aria-selected="false" aria-controls="panel-articles">{{ 'templates.search.articles' | t }}</button>
</div>

<div id="panel-all" class="search-results active" role="tabpanel" aria-labelledby="tab-all">
  {% if search.results.size > 0 %}
    {% for item in search.results %}
      {% if item.object_type == 'product' %}
        {% include 'product-card', product: item %}
      {% elsif item.object_type == 'article' %}
        <div class="search-article-card">
          <h3><a href="{{ item.url }}">{{ item.title }}</a></h3>
          <p>{{ item.excerpt_or_content | strip_html | truncate: 120 }}</p>
        </div>
      {% elsif item.object_type == 'page' %}
        <div class="search-page-card">
          <h3><a href="{{ item.url }}">{{ item.title }}</a></h3>
        </div>
      {% endif %}
    {% endfor %}
    {% include 'pagination' %}
  {% else %}
    <p class="search-no-results">{{ 'templates.search.no_results' | t }}</p>
    <p>{{ 'templates.search.suggestions' | t }}</p>
  {% endif %}
</div>

<div id="panel-products" class="search-results" role="tabpanel" aria-labelledby="tab-products" hidden>
  {% assign product_results = search.results | where: "object_type", "product" %}
  {% if product_results.size > 0 %}
    <div class="product-grid">
      {% for product in product_results %}
        {% include 'product-card', product: product %}
      {% endfor %}
    </div>
    {% include 'pagination' %}
  {% else %}
    <p>{{ 'templates.search.no_products' | t }}</p>
  {% endif %}
</div>

<div id="panel-articles" class="search-results" role="tabpanel" aria-labelledby="tab-articles" hidden>
  {% assign article_results = search.results | where: "object_type", "article" %}
  {% if article_results.size > 0 %}
    {% for article in article_results %}
      <div class="search-article-card">
        <h3><a href="{{ article.url }}">{{ article.title }}</a></h3>
        <p>{{ article.excerpt_or_content | strip_html | truncate: 120 }}</p>
      </div>
    {% endfor %}
    {% include 'pagination' %}
  {% else %}
    <p>{{ 'templates.search.no_articles' | t }}</p>
  {% endif %}
</div>

{% comment %} Static fallback for variant selector {% endcomment %}
<div class="variant-selector" data-product-json='{{ product | json }}'>
  {% for option in product.options_with_values %}
    <div class="variant-option">
      <label for="Option-{{ option.position }}">{{ option.name | t }}</label>

      {% if option.name == 'Color' or option.name == 'Colour' %}
        <div class="variant-swatches">
          {% for value in option.values %}
            <input type="radio"
                   id="swatch-{{ option.position }}-{{ value | handle }}"
                   name="option{{ option.position }}"
                   value="{{ value | escape }}"
                   {% if forloop.first %}checked{% endif %}>
            <label for="swatch-{{ option.position }}-{{ value | handle }}"
                   style="background-color: {{ value | replace: ' ', '' }}"
                   aria-label="{{ value | t }}">
            </label>
          {% endfor %}
        </div>
      {% else %}
        <select id="Option-{{ option.position }}" name="option{{ option.position }}">
          {% for value in option.values %}
            <option value="{{ value | escape }}" {% if forloop.first %}selected{% endif %}>{{ value | t }}</option>
          {% endfor %}
        </select>
      {% endif %}
    </div>
  {% endfor %}

  <input type="hidden" name="id" value="{{ product.selected_or_first_available_variant.id }}">
</div>

{% comment %} Usage: {% include 'menu-navigation', menu: 'main-menu' %} {% endcomment %}

{%- assign linklist = linklists[include.menu] -%}

<nav class="main-menu" aria-label="{{ 'sections.header.main_menu' | t }}">
  <ul class="main-menu__list">
    {% for link in linklist.links %}
      <li class="main-menu__item {% if link.links.size > 0 %}main-menu__item--has-dropdown{% endif %}">
        {% if link.links.size > 0 %}
          <button class="main-menu__toggle"
                  aria-haspopup="true"
                  aria-expanded="false"
                  aria-label="{{ link.title | t }} {{ 'sections.header.submenu_label' | t }}">
            {{ link.title | t }}
          </button>
          <ul class="main-menu__dropdown">
            {% for childlink in link.links %}
              <li>
                <a href="{{ childlink.url }}" class="main-menu__link">{{ childlink.title | t }}</a>
                {% if childlink.links.size > 0 %}
                  <ul class="main-menu__subdropdown">
                    {% for grandchild in childlink.links %}
                      <li><a href="{{ grandchild.url }}" class="main-menu__link">{{ grandchild.title | t }}</a></li>
                    {% endfor %}
                  </ul>
                {% endif %}
              </li>
            {% endfor %}
          </ul>
        {% else %}
          <a href="{{ link.url }}" class="main-menu__link">{{ link.title | t }}</a>
        {% endif %}
      </li>
    {% endfor %}
  </ul>
</nav>

{% layout 'blog' %}

<header class="blog-header">
  <h1>{{ blog.title }}</h1>
  {% if blog.description != blank %}
    <div class="blog-description rte">{{ blog.description }}</div>
  {% endif %}
</header>

<div class="blog-layout">
  <main class="blog-articles">
    {% if blog.articles.size > 0 %}
      <div class="article-grid">
        {% for article in blog.articles %}
          <article class="blog-article-card" aria-labelledby="article-title-{{ article.id }}">
            {% if article.image != blank %}
              <a href="{{ article.url }}" class="article-image-link">
                {%- assign img_url = article.image | image_url: width: 600 -%}
                <img src="{{ img_url }}&width=600"
                     alt="{{ article.image.alt | escape }}"
                     loading="lazy"
                     width="600"
                     height="400"
                     class="article-image">
              </a>
            {% endif %}

            <div class="article-content">
              <time datetime="{{ article.published_at | date: '%Y-%m-%d' }}" class="article-date">
                {{ article.published_at | date: format: 'month_day_year' }}
              </time>
              <h2 id="article-title-{{ article.id }}" class="article-title">
                <a href="{{ article.url }}">{{ article.title }}</a>
              </h2>
              {% if article.excerpt != blank %}
                <p class="article-excerpt">{{ article.excerpt | strip_html | truncate: 160 }}</p>
              {% else %}
                <p class="article-excerpt">{{ article.content | strip_html | truncate: 160 }}</p>
              {% endif %}
              {% if article.author != blank %}
                <p class="article-author">{{ 'blogs.article.by_author' | t: author: article.author }}</p>
              {% endif %}
            </div>
          </article>
        {% endfor %}
      </div>

      {% include 'pagination' %}
    {% else %}
      <p class="blog-empty">{{ 'blogs.general.no_articles' | t }}</p>
    {% endif %}
  </main>

  <aside class="blog-sidebar">
    {% if blog.tags.size > 0 %}
      <div class="blog-tags">
        <h3>{{ 'blogs.general.tags' | t }}</h3>
        <ul>
          {% for tag in blog.tags %}
            <li><a href="{{ blog.url }}/tagged/{{ tag | handle }}">{{ tag }}</a></li>
          {% endfor %}
        </ul>
      </div>
    {% endif %}
  </aside>
</div>

{% layout '404' %}

<main class="error-404" role="main" aria-label="{{ 'templates.404.aria_label' | t }}">
  <div class="error-content">
    <h1 class="error-title">{{ 'templates.404.title' | t }}</h1>
    <p class="error-message">{{ 'templates.404.subtext' | t }}</p>

    {% include 'search-bar' %}

    <a href="{{ routes.root_url }}" class="error-home-button">{{ 'templates.404.home' | t }}</a>

    {% if collections.size > 0 %}
      <div class="error-collections">
        <h2>{{ 'templates.404.popular_collections' | t }}</h2>
        <ul class="collection-list">
          {% for collection in collections limit: 3 %}
            <li><a href="{{ collection.url }}">{{ collection.title }}</a></li>
          {% endfor %}
        </ul>
      </div>
    {% endif %}
  </div>
</main>

{% if section.settings.video_url != blank %}
  {% assign video_id = section.settings.video_url | split: 'v=' | last | split: '&' | first %}
  {% if section.settings.video_url contains 'youtube.com' or section.settings.video_url contains 'youtu.be' %}
    {% assign embed_url = 'https://www.youtube.com/embed/' | append: video_id | append: '?autoplay=0&mute=1&rel=0' %}
    {% assign provider = 'YouTube' %}
  {% elsif section.settings.video_url contains 'vimeo.com' %}
    {% assign video_id = section.settings.video_url | split: '/' | last %}
    {% assign embed_url = 'https://player.vimeo.com/video/' | append: video_id | append: '?autoplay=0&muted=1' %}
    {% assign provider = 'Vimeo' %}
  {% endif %}
{% endif %}

<section class="video-section" aria-label="{{ 'sections.video.aria_label' | t }}">
  <div class="video-wrapper" style="position: relative; padding-bottom: 56.25%; height: 0; overflow: hidden;">
    {% if embed_url %}
      <iframe src="{{ embed_url }}"
              title="{{ section.settings.title | default: 'Video' | escape }}"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
              style="position: absolute; top: 0; left: 0; width: 100%; height: 100%; border: 0;"
              loading="lazy">
      </iframe>
    {% else %}
      {% if section.settings.placeholder_image != blank %}
        {%- assign img_url = section.settings.placeholder_image | image_url: width: 1200 -%}
        <img srcset="{{ img_url }}&width=600 600w,
                      {{ img_url }}&width=1200 1200w"
             src="{{ img_url }}&width=1200"
             alt="{{ section.settings.placeholder_image.alt | escape }}"
             loading="lazy"
             width="1200"
             height="675"
             style="width: 100%; height: auto; display: block;">
      {% else %}
        <div class="video-placeholder" aria-hidden="true" style="background: #f5f5f5; width: 100%; height: 100%; display: flex; align-items: center; justify-content: center;">
          {{ 'sections.video.no_video' | t }}
        </div>
      {% endif %}
    {% endif %}

    <div class="video-overlay">
      {% if section.settings.title != blank %}
        <h2 class="video-title">{{ section.settings.title | t }}</h2>
      {% endif %}
      {% if section.settings.subtitle != blank %}
        <p class="video-subtitle">{{ section.settings.subtitle | t }}</p>
      {% endif %}
    </div>
  </div>
</section>

{% schema %}
{
  "name": "{{'sections.video.name' | t}}",
  "tag": "section",
  "class": "video-section",
  "settings": [
    {
