{% comment %} Basic Slideshow Section {% endcomment %}
<section class="slideshow-section" aria-labelledby="slideshow-heading">
  <h2 id="slideshow-heading" class="visually-hidden">{{ 'sections.slideshow.heading' | t }}</h2>
  {% if section.blocks.size > 0 %}
    <div class="slideshow-carousel" data-autoplay="true" data-pause-on-hover="true">
      {% for block in section.blocks %}
        {% if block.type == 'slide' %}
          <div class="slide" role="group" aria-roledescription="slide" aria-label="{{ forloop.index }} {{ 'general.of' | t }} {{ section.blocks.size }}">
            {% if block.settings.image != blank %}
              {% assign img_url = block.settings.image | image_url: width: 1920 %}
              <img src="{{ img_url }}" alt="{{ block.settings.image.alt | default: 'sections.slideshow.slide_alt' | t }}" width="1920" height="1080" loading="lazy" srcset="{{ block.settings.image | image_url: width: 768 }} 768w, {{ img_url }} 1920w">
            {% else %}
              <div class="placeholder-image">{{ 'sections.slideshow.placeholder' | t }}</div>
            {% endif %}
            <div class="slide-content">
              <h3>{{ block.settings.heading | default: 'sections.slideshow.default_heading' | t }}</h3>
              <p>{{ block.settings.subheading | default: 'sections.slideshow.default_subheading' | t }}</p>
              {% if block.settings.button_link != blank %}
                <a href="{{ block.settings.button_link }}" class="button" aria-label="{{ block.settings.button_label | default: 'sections.slideshow.default_button' | t }}">{{ block.settings.button_label | default: 'sections.slideshow.default_button' | t }}</a>
              {% endif %}
            </div>
          </div>
        {% endif %}
      {% endfor %}
      <button class="prev" aria-label="{{ 'sections.slideshow.previous' | t }}">Previous</button>
      <button class="next" aria-label="{{ 'sections.slideshow.next' | t }}">Next</button>
      <div class="dots" role="tablist"></div>
    </div>
  {% else %}
    <p class="empty-state">{{ 'sections.slideshow.no_slides' | t }}</p>
  {% endif %}
</section>

{% schema %}
{
  "name": "{{ 'sections.slideshow.name' | t }}",
  "tag": "section",
  "class": "section-slideshow",
  "max_blocks": 3,
  "blocks": [
    {
      "type": "slide",
      "name": "{{ 'sections.slideshow.slide' | t }}",
      "settings": [
        {
          "type": "image_picker",
          "id": "image",
          "label": "{{ 'sections.slideshow.image' | t }}"
