{% comment %} Intermediate Product Card Snippet {% endcomment %}
{% assign product = include.product %}
{% if product != blank %}
  <div class="product-card" role="article" aria-labelledby="product-title-{{ product.id }}">
    <a href="{{ product.url }}" aria-label="{{ product.title | escape }}">
      {% if product.featured_image != blank %}
        {% assign img_url = product.featured_image | image_url: width: 300 %}
        <img src="{{ img_url }}" alt="{{ product.featured_image.alt | default: product.title | escape }}" width="300" height="300" loading="lazy" srcset="{{ product.featured_image | image_url: width: 150 }} 150w, {{ img_url }} 300w">
      {% else %}
        <div class="placeholder-image">{{ 'snippets.product_card.no_image' | t }}</div>
      {% endif %}
    </a>
    <h3 id="product-title-{{ product.id }}">{{ product.title | truncate: 50 }}</h3>
    <p class="vendor">{{ product.vendor }}</p>
    <div class="price">
      {% if product.compare_at_price > product.price %}
        <span class="compare-at">{{ product.compare_at_price | money }}</span>
      {% endif %}
      <span class="current-price">{{ product.price | money }}</span>
      {% if product.price == 0 %}
        <span class="free">{{ 'snippets.product_card.free' | t }}</span>
      {% endif %}
    </div>
    {% if product.available %}
      <form method="post" action="/cart/add">
        <select name="id" aria-label="{{ 'snippets.product_card.variant_label' | t }}">
          {% for variant in product.variants %}
            <option value="{{ variant.id }}" {% if variant.available == false %}disabled{% endif %}>{{ variant.title }} {% if variant.available == false %}- {{ 'snippets.product_card.sold_out' | t }}{% endif %}</option>
          {% endfor %}
        </select>
        <button type="submit" aria-label="{{ 'snippets.product_card.add_to_cart' | t }}">{{ 'snippets.product_card.add_to_cart' | t }}</button>
      </form>
    {% else %}
      <p class="sold-out">{{ 'snippets.product_card.sold_out' | t }}</p>
    {% endif %}
    <a href="{{ product.url }}" class="quick-view" aria-label="{{ 'snippets.product_card.quick_view' | t }}">{{ 'snippets.product_card.quick_view' | t }}</a>
  </div>
{% else %}
  <p class="empty-state">{{ 'snippets.product_card.no_product' | t }}</p>
{% endif %}

