</ul>

{% comment %} Intermediate Pagination Snippet {% endcomment %}
{% if paginate.pages > 1 %}
  <nav class="pagination" role="navigation" aria-label="{{ 'snippets.pagination.label' | t }}">
    {% if paginate.previous %}
      <a href="{{ paginate.previous.url }}" aria-label="{{ 'snippets.pagination.previous' | t }}">{{ 'snippets.pagination.previous' | t }}</a>
    {% else %}
      <span class="disabled">{{ 'snippets.pagination.previous' | t }}</span>
    {% endif %}
    {% for part in paginate.parts %}
      {% if part.is_link %}
        <a href="{{ part.url }}" aria-label="{{ 'snippets.pagination.page' | t: number: part.title }}">{{ part.title }}</a>
      {% elsif part.title == paginate.current_page %}
        <span class="current" aria-current="page">{{ part.title }}</span>
      {% else %}
        <span>{{ part.title }}</span>
      {% endif %}
    {% endfor %}
    {% if paginate.next %}
      <a href="{{ paginate.next.url }}" aria-label="{{ 'snippets.pagination.next' | t }}">{{ 'snippets.pagination.next' | t }}</a>
    {% else %}
      <span class="disabled">{{ 'snippets.pagination.next' | t }}</span>
    {% endif %}
  </nav>
{% else %}
  <p class="empty-state">{{ 'snippets.pagination.no_pages' | t }}</p>
{% endif %}

{% comment %} Intermediate Breadcrumbs Snippet {% endcomment %}
{% if template != 'index' %}
  <nav class="breadcrumbs" role="navigation" aria-label="{{ 'snippets.breadcrumbs.label' | t }}">
    <ol itemscope itemtype="https://schema.org/BreadcrumbList">
      <li itemprop="itemListElement" itemscope itemtype="https://schema.org/ListItem">
        <a itemprop="item" href="/" aria-label="{{ 'snippets.breadcrumbs.home' | t }}"><span itemprop="name">{{ 'snippets.breadcrumbs.home' | t }}</span></a>
        <meta itemprop="position" content="1" />
      </li>
      {% if collection %}
        <li itemprop="itemListElement" itemscope itemtype="https://schema.org/ListItem">
          <a itemprop="item" href="{{ collection.url }}"><span itemprop="name">{{ collection.title | truncate: 50 }}</span></a>
          <meta itemprop="position" content="2" />
        </li>
      {% endif %}
      {% if product %}
        <li itemprop="itemListElement" itemscope itemtype="https://schema.org/ListItem">
          <span itemprop="name">{{ product.title | truncate: 50 }}</span>
          <meta itemprop="position" content="3" />
        </li>
      {% endif %}
      <!-- Add more conditions for other templates -->
    </ol>
  </nav>
{% else %}
  <p class="empty-state">{{ 'snippets.breadcrumbs.no_crumbs' | t }}</p>
{% endif %}

{% comment %} Advanced Product Template {% endcomment %}
{% assign product = product %}
{% if product != blank %}
  <div class="product-template" itemscope itemtype="https://schema.org/Product">
    <meta itemprop="name" content="{{ product.title }}">
    <meta itemprop="url" content="{{ shop.url }}{{ product.url }}">
    <meta itemprop="image" content="{{ product.featured_image.src | image_url }}">
    <!-- Gallery -->
    <div class="product-gallery">
      {% if product.images.size > 0 %}
        <div class="main-image">
          <img src="{{ product.featured_image | image_url: width: 600 }}" alt="{{ product.featured_image.alt | escape }}" id="main-image" loading="eager">
        </div>
        <div class="thumbnails">
          {% for image in product.images %}
            <img src="{{ image | image_url: width: 100 }}" alt="{{ image.alt | escape }}" data-large="{{ image | image_url: width: 600 }}" loading="lazy">
          {% endfor %}
        </div>
      {% else %}
        <div class="placeholder-image">{{ 'templates.product.no_image' | t }}</div>
      {% endif %}
    </div>
    <!-- Details -->
    <div class="product-details">
      <h1>{{ product.title }}</h1>
      <div class="price" itemprop="offers" itemscope itemtype="https://schema.org/Offer">
        <span itemprop="price">{{ product.price | money }}</span>
        {% if product.compare_at_price > product.price %}
          <span class="compare-at">{{ product.compare_at_price | money }}</span>
        {% endif %}
      </div>
      {% form 'product', product %}
        {% for option in product.options_with_values %}
          <label for="option-{{ forloop.index }}">{{ option.name }}</label>
          <select id="option-{{ forloop.index }}" name="options[{{ option.name | escape }}]">
            {% for value in option.values %}
              <option>{{ value }}</option>
            {% endfor %}
          </select>
        {% endfor %}
        <label for="quantity">{{ 'templates.product.quantity' | t }}</label>
        <input type="number" id="quantity" name="quantity" value="1" min="1">
        <button type="submit" {% unless current_variant.available %}disabled{% endunless %}>{{ 'templates.product.add_to_cart' | t }}</button>
      {% endform %}
      <!-- Description Tabs -->
      <div class="tabs">
        <button role="tab" aria-controls="description">{{ 'templates.product.description' | t }}</button>
        <div id="description" role="tabpanel">{{ product.description }}</div>
        <!-- More tabs -->
      </div>
    </div>
    <!-- Related Products -->
    {% if collection.products.size > 1 %}
      <section class="related-products">
        <h2>{{ 'templates.product.related' | t }}</h2>
        <div class="carousel">
          {% for related in collections[product.collections.first.handle].products limit: 4 %}
            {% unless related.handle == product.handle %}
              {% include 'product-card' with product: related %}
            {% endunless %}
          {% endfor %}
        </div>
      </section>
    {% else %}
      <p>{{ 'templates.product.no_related' | t }}</p>
    {% endif %}
    <!-- Reviews -->
    <section class="reviews">
      <!-- Integrate reviews app or placeholder -->
      <p>{{ 'templates.product.no_reviews' | t }}</p>
    </section>
  </div>
{% else %}
  <p class="empty-state">{{ 'templates.product.no_product' | t }}</p>
{% endif %}
{% schema %} { "name": "Product" } {% endschema %}

{% comment %} Advanced Collection Template {% endcomment %}
{% paginate collection.products by 24 %}
  <div class="collection-template">
    <header>
      <h1>{{ collection.title }}</h1>
      <p>{{ collection.products_count }} {{ 'templates.collection.product_count' | t }}</p>
      <!-- Sort -->
      <select aria-label="{{ 'templates.collection.sort_label' | t }}">
        <option value="manual">{{ 'templates.collection.sort_featured' | t }}</option>
        <option value="price-ascending">{{ 'templates.collection.sort_price_low' | t }}</option>
        <!-- More options -->
      </select>
      <!-- View Toggle -->
      <button aria-label="{{ 'templates.collection.grid_view' | t }}">Grid</button>
      <button aria-label="{{ 'templates.collection.list_view' | t }}">List</button>
    </header>
    <!-- Filters -->
    <aside class="filters">
      <h2>{{ 'templates.collection.filters' | t }}</h2>
      <!-- Tag filters -->
      {% for tag in collection.all_tags %}
        <label>
          <input type="checkbox" value="{{ tag | handle }}"> {{ tag }}
        </label>
      {% endfor %}
      <!-- Price range -->
      <input type="range" min="0" max="{{ collection.products.first.price_max }}" aria-label="{{ 'templates.collection.price_filter' | t }}">
    </aside>
    <!-- Products -->
    <div class="products-grid">
      {% if collection.products.size > 0 %}
        {% for product in collection.products %}
          {% include 'product-card' with product: product %}
        {% endfor %}
      {% else %}
        <p class="empty-state">{{ 'templates.collection.no_products' | t }}</p>
      {% endif %}
    </div>
    <!-- Pagination -->
    {% include 'pagination' %}
  </div>
{% endpaginate %}
