{% comment %} Usage: {% include 'inventory-status', variant: product.selected_or_first_available_variant %} {% endcomment %}

{%- assign variant = include.variant -%}

<div class="inventory-status" role="status" aria-live="polite">
  {% if variant.available %}
    {% if variant.inventory_management == blank or variant.inventory_policy == 'continue' %}
      <span class="inventory-in-stock">{{ 'products.product.in_stock' | t }}</span>
    {% elsif variant.inventory_quantity > 0 %}
      {% if variant.inventory_quantity <= 5 %}
        <span class="inventory-low-stock">{{ 'products.product.low_stock' | t: count: variant.inventory_quantity }}</span>
      {% else %}
        <span class="inventory-in-stock">{{ 'products.product.in_stock' | t }}</span>
      {% endif %}
    {% else %}
      <span class="inventory-out-of-stock">{{ 'products.product.sold_out' | t }}</span>
    {% endif %}
  {% else %}
    <span class="inventory-out-of-stock">{{ 'products.product.sold_out' | t }}</span>
  {% endif %}
</div>

{% comment %} Assumes metafield: product.metafields.reviews.rating.value (number) {% endcomment %}

{%- assign rating = product.metafields.reviews.rating.value -%}
{%- assign rating = rating | round: 1 -%}

{% if rating > 0 %}
  <div class="rating-stars" aria-label="{{ 'products.product.rating_label' | t: rating: rating, max_rating: 5 }}">
    {% for i in (1..5) %}
      {% if rating >= i %}
        {% include 'icon' with 'star-full' %}
      {% elsif rating > i - 1 %}
        {% include 'icon' with 'star-half' %}
      {% else %}
        {% include 'icon' with 'star-empty' %}
      {% endif %}
    {% endfor %}

    {% assign review_count = product.metafields.reviews.review_count.value %}
    {% if review_count > 0 %}
      <span class="rating-count">({{ review_count }})</span>
    {% endif %}
  </div>
{% endif %}

{% layout 'gift_card' %}

<main class="gift-card-page" role="main">
  {% if gift_card.passed %}
    <div class="gift-card-claimed">
      <h1>{{ 'gift_cards.show.title' | t }}</h1>
      <p class="gift-card-code">{{ 'gift_cards.show.code' | t: code: gift_card.code }}</p>
      <p class="gift-card-balance">{{ 'gift_cards.show.balance' | t: balance: gift_card.balance }}</p>
      <a href="{{ shop.url }}" class="gift-card-shop-button">{{ 'gift_cards.show.shop_now' | t }}</a>
    </div>
  {% else %}
    <div class="gift-card-claim">
      <h1>{{ 'gift_cards.claim.title' | t }}</h1>
      <p>{{ 'gift_cards.claim.intro' | t }}</p>

      {% form 'gift_card' %}
        {% if form.errors %}
          <div class="form-error" role="alert">{{ 'gift_cards.claim.invalid_code' | t }}</div>
        {% endif %}

        <div class="field-group">
          <label for="GiftCardCode">{{ 'gift_cards.claim.code_label' | t }}</label>
          <input type="text" id="GiftCardCode" name="gift_card_code" value="{{ gift_card.code }}" required>
        </div>

        <button type="submit" class="gift-card-button">{{ 'gift_cards.claim.submit' | t }}</button>
      {% endform %}

      <div class="gift-card-terms">
        {{ 'gift_cards.claim.terms_html' | t }}
      </div>
    </div>
  {% endif %}
</main>

{% schema %}
{
  "name": "Header Navigation",
  "tag": "section",
  "class": "header-section",
  "settings": [
    {
      "type": "image_picker",
      "id": "logo",
      "label": "Logo"
    },
    {
      "type": "text",
      "id": "logo_text",
      "label": "Logo text (if no logo)",
      "default": "Your Store"
    },
    {
      "type": "url",
      "id": "logo_link",
      "label": "Logo link",
      "default": "/"
    },
    {
      "type": "link_list",
      "id": "menu",
      "label": "Main menu",
      "default": "main-menu"
    },
    {
      "type": "checkbox",
      "id": "enable_search",
      "label": "Enable search",
      "default": true
    },
    {
      "type": "checkbox",
      "id": "enable_sticky",
      "label": "Enable sticky header",
      "default": true
    }
  ],
  "presets": [
    {
      "name": "Header Navigation"
    }
  ]
}
{% endschema %}

<header class="header{% if section.settings.enable_sticky %} header--sticky{% endif %}" data-header>
  <div class="header__container">
    <div class="header__mobile-toggle">
      <button class="header__hamburger" data-mobile-toggle aria-label="Toggle menu" aria-expanded="false">
        <span class="header__hamburger-line"></span>
        <span class="header__hamburger-line"></span>
        <span class="header__hamburger-line"></span>
      </button>
    </div>

    <div class="header__logo">
      <a href="{{ section.settings.logo_link }}" class="header__logo-link">
        {% if section.settings.logo != blank %}
          <img src="{{ section.settings.logo | image_url: width: 200 }}"
               alt="{{ shop.name | escape }}"
               class="header__logo-image"
               width="200"
               height="50">
        {% else %}
          <span class="header__logo-text">{{ section.settings.logo_text | escape }}</span>
        {% endif %}
      </a>
    </div>

    <nav class="header__nav" data-main-nav>
      <ul class="header__menu" role="list">
        {% for link in section.settings.menu.links %}
          <li class="header__menu-item{% if link.links.size > 0 %} header__menu-item--has-dropdown{% endif %}">
            <a href="{{ link.url }}"
               class="header__menu-link{% if link.current %} header__menu-link--active{% endif %}"
               {% if link.links.size > 0 %}aria-haspopup="true" aria-expanded="false"{% endif %}>
              {{ link.title | escape }}
              {% if link.links.size > 0 %}
                <span class="header__menu-arrow" aria-hidden="true">▼</span>
              {% endif %}
            </a>

            {% if link.links.size > 0 %}
              <ul class="header__dropdown" role="list">
                {% for childlink in link.links %}
                  <li class="header__dropdown-item{% if childlink.links.size > 0 %} header__dropdown-item--has-sub{% endif %}">
                    <a href="{{ childlink.url }}"
                       class="header__dropdown-link{% if childlink.current %} header__dropdown-link--active{% endif %}"
                       {% if childlink.links.size > 0 %}aria-haspopup="true" aria-expanded="false"{% endif %}>
                      {{ childlink.title | escape }}
                      {% if childlink.links.size > 0 %}
                        <span class="header__dropdown-arrow" aria-hidden="true">▶</span>
                      {% endif %}
                    </a>

                    {% if childlink.links.size > 0 %}
                      <ul class="header__submenu" role="list">
                        {% for grandchildlink in childlink.links %}
                          <li class="header__submenu-item">
                            <a href="{{ grandchildlink.url }}"
                               class="header__submenu-link{% if grandchildlink.current %} header__submenu-link--active{% endif %}">
                              {{ grandchildlink.title | escape }}
                            </a>
                          </li>
                        {% endfor %}
                      </ul>
                    {% endif %}
                  </li>
                {% endfor %}
              </ul>
            {% endif %}
          </li>
        {% endfor %}
      </ul>
    </nav>

    <div class="header__actions">
      {% if section.settings.enable_search %}
        <div class="header__search" data-search>
          <button class="header__search-toggle" data-search-toggle aria-label="Toggle search">
            <svg class="header__search-icon" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path d="M8 3a5 5 0 100 10 5 5 0 000-10zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z"/>
            </svg>
          </button>

          <div class="header__search-dropdown" data-search-dropdown>
            <form action="/search" method="get" class="header__search-form" role="search">
              <input type="search"
                     name="q"
                     placeholder="Search products..."
                     class="header__search-input"
                     aria-label="Search products"
                     autocomplete="off">
              <button type="submit" class="header__search-submit" aria-label="Submit search">
                <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
                  <path d="M8 3a5 5 0 100 10 5 5 0 000-10zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z"/>
                </svg>
              </button>
            </form>
          </div>
        </div>
      {% endif %}

      <a href="/cart" class="header__cart" aria-label="Shopping cart">
        <svg class="header__cart-icon" width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
          <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z"/>
        </svg>
        <span class="header__cart-count" data-cart-count>{{ cart.item_count }}</span>
      </a>
    </div>
  </div>

  <div class="header__mobile-menu" data-mobile-menu>
    <div class="header__mobile-header">
      <span class="header__mobile-title">Menu</span>
      <button class="header__mobile-close" data-mobile-close aria-label="Close menu">
        <span class="header__mobile-close-icon" aria-hidden="true">×</span>
      </button>
    </div>

    <nav class="header__mobile-nav">
      <ul class="header__mobile-list" role="list">
        {% for link in section.settings.menu.links %}
          <li class="header__mobile-item{% if link.links.size > 0 %} header__mobile-item--has-children{% endif %}">
            <div class="header__mobile-link-wrapper">
              <a href="{{ link.url }}" class="header__mobile-link">{{ link.title | escape }}</a>
              {% if link.links.size > 0 %}
                <button class="header__mobile-toggle" data-mobile-toggle-sub aria-expanded="false">
                  <span class="header__mobile-toggle-icon" aria-hidden="true">+</span>
                </button>
              {% endif %}
            </div>

            {% if link.links.size > 0 %}
              <ul class="header__mobile-dropdown" role="list">
                {% for childlink in link.links %}
                  <li class="header__mobile-dropdown-item{% if childlink.links.size > 0 %} header__mobile-dropdown-item--has-children{% endif %}">
                    <a href="{{ childlink.url }}" class="header__mobile-dropdown-link">{{ childlink.title | escape }}</a>

                    {% if childlink.links.size > 0 %}
                      <ul class="header__mobile-submenu" role="list">
                        {% for grandchildlink in childlink.links %}
                          <li class="header__mobile-submenu-item">
                            <a href="{{ grandchildlink.url }}" class="header__mobile-submenu-link">{{ grandchildlink.title | escape }}</a>
                          </li>
                        {% endfor %}
                      </ul>
                    {% endif %}
                  </li>
                {% endfor %}
              </ul>
            {% endif %}
          </li>
        {% endfor %}
      </ul>
    </nav>
  </div>
</header>

