<script>
document.addEventListener('DOMContentLoaded', function() {
  const mobileToggle = document.querySelector('[data-mobile-toggle]');
  const mobileMenu = document.querySelector('[data-mobile-menu]');
  const mobileClose = document.querySelector('[data-mobile-close]');
  const searchToggle = document.querySelector('[data-search-toggle]');
  const searchDropdown = document.querySelector('[data-search-dropdown]');

  // Mobile menu toggle
  mobileToggle.addEventListener('click', function() {
    const isOpen = mobileMenu.classList.contains('header__mobile-menu--open');
    mobileMenu.classList.toggle('header__mobile-menu--open');
    this.setAttribute('aria-expanded', !isOpen);
    document.body.style.overflow = isOpen ? '' : 'hidden';
  });

  mobileClose.addEventListener('click', function() {
    mobileMenu.classList.remove('header__mobile-menu--open');
    mobileToggle.setAttribute('aria-expanded', 'false');
    document.body.style.overflow = '';
  });

  // Search toggle
  searchToggle.addEventListener('click', function() {
    searchDropdown.style.display = searchDropdown.style.display === 'block' ? 'none' : 'block';
    if (searchDropdown.style.display === 'block') {
      searchDropdown.querySelector('input').focus();
    }
  });

  // Mobile submenu toggles
  const mobileSubToggles = document.querySelectorAll('[data-mobile-toggle-sub]');
  mobileSubToggles.forEach(toggle => {
    toggle.addEventListener('click', function() {
      const isExpanded = this.getAttribute('aria-expanded') === 'true';
      this.setAttribute('aria-expanded', !isExpanded);

      const icon = this.querySelector('.header__mobile-toggle-icon');
      icon.style.transform = isExpanded ? '' : 'rotate(45deg)';

      const submenu = this.closest('.header__mobile-item').querySelector('.header__mobile-dropdown');
      submenu.classList.toggle('header__mobile-dropdown--open');
    });
  });

  // Close mobile menu on escape
  document.addEventListener('keydown', function(e) {
    if (e.key === 'Escape') {
      mobileMenu.classList.remove('header__mobile-menu--open');
      mobileToggle.setAttribute('aria-expanded', 'false');
      document.body.style.overflow = '';
    }
  });
});
</script>

