      <div class="faq__categories" data-faq-categories>
        <button class="faq__category-button faq__category-button--active"
                data-category="all"
                aria-selected="true">
          All
        </button>
        {% for block in section.blocks %}
          {% if block.type == 'category' %}
            <button class="faq__category-button"
                    data-category="{{ block.settings.name | escape }}"
                    aria-selected="false">
              {{ block.settings.name | escape }}
            </button>
          {% endif %}
        {% endfor %}
      </div>
    {% endif %}

    <div class="faq__items" data-faq-items>
      {% assign faq_items = section.blocks | where: 'type', 'faq_item' %}
      {% if faq_items.size > 0 %}
        {% for block in faq_items %}
          <div class="faq__item"
               data-faq-item
               data-category="{{ block.settings.category | escape }}"
               data-question="{{ block.settings.question | escape }}"
               data-answer="{{ block.settings.answer | escape }}">
            <button class="faq__question"
                    data-faq-question
                    aria-expanded="false"
                    aria-controls="faq-answer-{{ section.id }}-{{ forloop.index }}">
              <span class="faq__question-text">{{ block.settings.question | escape }}</span>
              <span class="faq__icon" aria-hidden="true">
                <svg class="faq__icon-plus" width="16" height="16" viewBox="0 0 16 16" fill="currentColor">
                  <path d="M8 3v10M3 8h10" stroke="currentColor" stroke-width="2" fill="none"/>
                </svg>
                <svg class="faq__icon-minus hidden" width="16" height="16" viewBox="0 0 16 16" fill="currentColor">
                  <path d="M3 8h10" stroke="currentColor" stroke-width="2" fill="none"/>
                </svg>
              </span>
            </button>
            <div class="faq__answer"
                 id="faq-answer-{{ section.id }}-{{ forloop.index }}"
                 data-faq-answer
                 aria-hidden="true">
              <div class="faq__answer-content">
                {{ block.settings.answer | escape }}
              </div>
            </div>
          </div>
        {% endfor %}
      {% else %}
        <div class="faq__empty">
          <p>No FAQs have been added yet.</p>
        </div>
      {% endif %}
    </div>

    <div class="faq__no-results hidden" data-faq-no-results>
      <p>No FAQs match your search. Try different keywords.</p>
    </div>
  </div>
</div>

