<script>
document.addEventListener('DOMContentLoaded', function() {
  const faqSection = document.querySelector('[data-faq-section]');
  if (!faqSection) return;

  const searchInput = faqSection.querySelector('[data-faq-search]');
  const categoryButtons = faqSection.querySelectorAll('[data-category]');
  const faqItems = faqFaSection.querySelectorAll('[data-faq-item]');
  const faqQuestions = faqSection.querySelectorAll('[data-faq-question]');
  const noResults = faqSection.querySelector('[data-faq-no-results]');

  // Accordion functionality
  faqQuestions.forEach(question => {
    question.addEventListener('click', function() {
      const isExpanded = this.getAttribute('aria-expanded') === 'true';
      const answer = this.nextElementSibling;
      const icons = this.querySelectorAll('.faq__icon svg');

      // Close all other items
      faqQuestions.forEach(q => {
        if (q !== this) {
          q.setAttribute('aria-expanded', 'false');
          q.nextElementSibling.setAttribute('aria-hidden', 'true');
          q.querySelector('.faq__icon-plus').classList.remove('hidden');
          q.querySelector('.faq__icon-minus').classList.add('hidden');
        }
      });

      // Toggle current item
      this.setAttribute('aria-expanded', !isExpanded);
      answer.setAttribute('aria-hidden', isExpanded);

      icons.forEach(icon => icon.classList.toggle('hidden'));
    });
  });

  // Search functionality
  if (searchInput) {
    searchInput.addEventListener('input', function() {
      const searchTerm = this.value.toLowerCase().trim();
      let hasVisibleItems = false;

      faqItems.forEach(item => {
        const question = item.dataset.question.toLowerCase();
        const answer = item.dataset.answer.toLowerCase();
        const isMatch = question.includes(searchTerm) || answer.includes(searchTerm);

        item.style.display = isMatch ? 'block' : 'none';
        if (isMatch) hasVisibleItems = true;
      });

      noResults.classList.toggle('hidden', hasVisibleItems || searchTerm === '');
    });
  }

  // Category filtering
  categoryButtons.forEach(button => {
    button.addEventListener('click', function() {
      const category = this.dataset.category;
      const isActive = this.classList.contains('faq__category-button--active');

      if (isActive) return;

      // Update active button
      categoryButtons.forEach(btn => {
        btn.classList.remove('faq__category-button--active');
        btn.setAttribute('aria-selected', 'false');
      });
      this.classList.add('faq__category-button--active');
      this.setAttribute('aria-selected', 'true');

      // Filter items
      faqItems.forEach(item => {
        const itemCategory = item.dataset.category;
        const shouldShow = category === 'all' || itemCategory === category;
        item.style.display = shouldShow ? 'block' : 'none';
      });

      // Reset search
      if (searchInput) {
        searchInput.value = '';
        searchInput.dispatchEvent(new Event('input'));
      }
    });
  });

  // Keyboard navigation
  faqQuestions.forEach(question => {
    question.addEventListener('keydown', function(e) {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        this.click();
      }
    });
  });
});
</script>

