{% comment %} Intermediate Variant Selector Snippet {% endcomment %}
{% assign current_variant = product.selected_or_first_available_variant %}
<div class="variant-selector" data-product-id="{{ product.id }}">
  {% for option in product.options_with_values %}
    <fieldset class="variant-option">
      <legend>{{ option.name }}</legend>
      {% if option.name contains 'Color' or option.name contains 'colour' %}
        <div class="swatch-group" role="radiogroup" aria-label="{{ option.name }}">
          {% for value in option.values %}
            {% assign variant = product.variants | where: option.name, value | first %}
            <label class="swatch {% if current_variant == variant %}selected{% endif %}" for="{{ option.name | handle }}-{{ forloop.index }}">
              <input 
                type="radio" 
                id="{{ option.name | handle }}-{{ forloop.index }}" 
                name="{{ option.name | handle }}" 
                value="{{ value | escape }}" 
                {% if current_variant == variant %}checked{% endif %}
                {% unless variant.available %}disabled{% endunless %}
                aria-label="{{ value }} {% unless variant.available %}{{ 'snippets.variant_selector.sold_out' | t }}{% endunless %}"
              >
              <span class="swatch-color" style="background-color: {{ value | downcase | prepend: '#' | default: '#ccc' }};"></span>
              <span class="sr-only">{{ value }}</span>
            </label>
          {% endfor %}
        </div>
      {% else %}
        <select name="{{ option.name | handle }}" aria-label="{{ option.name }}">
          {% for value in option.values %}
            {% assign variant = product.variants | where: option.name, value | first %}
            <option 
              value="{{ value | escape }}" 
              {% if current_variant == variant %}selected{% endif %}
              {% unless variant.available %}disabled{% endunless %}
            >
              {{ value }}{% unless variant.available %} - {{ 'snippets.variant_selector.sold_out' | t }}{% endunless %}
            </option>
          {% endfor %}
        </select>
      {% endif %}
    </fieldset>
  {% endfor %}
  <p class="availability-message" aria-live="polite">
    {% if current_variant.available %}
      {{ 'snippets.variant_selector.in_stock' | t }}
    {% else %}
      {{ 'snippets.variant_selector.out_of_stock' | t }}
    {% endif %}
  </p>
</div>

