          <a href="{{ collection.url }}" class="button" aria-label="{{ 'sections.featured_collection.view_all' | t: title: collection.title }}">
            {{ 'sections.featured_collection.view_all' | t }}
          </a>
        </div>
      {% endif %}
    {% else %}
      <p class="empty-state">{{ 'sections.featured_collection.empty' | t }}</p>
    {% endif %}
  {% else %}
    <p class="empty-state">{{ 'sections.featured_collection.no_collection' | t }}</p>
  {% endif %}
</section>

{% schema %}
{
  "name": "{{ 'sections.featured_collection.name' | t }}",
  "tag": "section",
  "class": "section-featured-collection",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "{{ 'sections.featured_collection.heading' | t }}",
      "default": "{{ 'sections.featured_collection.default_heading' | t }}"
    },
    {
      "type": "text",
      "id": "subheading",
      "label": "{{ 'sections.featured_collection.subheading' | t }}"
    },
    {
      "type": "collection",
      "id": "collection",
      "label": "{{ 'sections.featured_collection.collection' | t }}"
    },
    {
      "type": "range",
      "id": "products_to_show",
      "min": 4,
      "max": 12,
      "step": 2,
      "label": "{{ 'sections.featured_collection.products_to_show' | t }}",
      "default": 8
    }
  ],
  "presets": [
    {
      "name": "{{ 'sections.featured_collection.name' | t }}",
      "settings": {
        "products_to_show": 8
      }
    }
