}
{% endschema %}

{% assign size_chart = product.metafields.size_chart.table %}

{% if size_chart != blank %}
  <div class="size-chart">
    <button type="button" class="size-chart__button" aria-expanded="false" aria-controls="size-chart-table">
      {{ 'products.product.size_guide' | t }}
    </button>
    <div id="size-chart-table" class="size-chart__table" hidden>
      {{ size_chart }}
    </div>
  </div>
{% endif %}

{% if product.available == false %}
  <div class="back-in-stock">
    <p>{{ 'products.product.notify_when_available' | t }}</p>
    {% form 'contact' %}
      <input type="hidden" name="contact[tags]" value="backinstock,{{ product.handle }}">
      <div class="field-group">
        <label for="BackInStockEmail">{{ 'products.product.email_label' | t }}</label>
        <input type="email" id="BackInStockEmail" name="contact[email]" required>
      </div>
      <button type="submit">{{ 'products.product.notify_me' | t }}</button>
