{% if variant.inventory_management != blank and variant.inventory_policy == 'deny' and variant.inventory_quantity <= 10 %}
  <div class="product-quantity-hint" role="status" aria-live="polite">
    {% if variant.inventory_quantity > 0 %}
      {{ 'products.product.quantity_left' | t: count: variant.inventory_quantity }}
