      {{ 'products.product.out_of_stock' | t }}
    {% endif %}
  </div>
{% endif %}

{% layout 'account' %}

<header class="account-header">
  <h1>{{ 'customer.addresses.title' | t }}</h1>
</header>

{% if customer.addresses.size > 0 %}
  <div class="account-addresses">
    {% paginate customer.addresses by 10 %}
      <ul class="address-list">
        {% for address in customer.addresses %}
          <li class="address-card" id="Address-{{ address.id }}">
            <p><strong>{{ address.first_name }} {{ address.last_name }}</strong></p>
            <p>{{ address.company }}</p>
            <p>{{ address.street }}</p>
            <p>{{ address.city }}, {{ address.province_code }} {{ address.zip }}</p>
            <p>{{ address.country }}</p>
            <p>{{ address.phone }}</p>

            {% if address == customer.default_address %}
              <p class="address-default" aria-label="{{ 'customer.addresses.default_address' | t }}">
                ★ {{ 'customer.addresses.default' | t }}
              </p>
            {% endif %}

            <div class="address-actions">
              <a href="{{ routes.account_addresses_edit_url }}?address={{ address.id }}" class="address-edit">{{ 'customer.addresses.edit' | t }}</a>
              <a href="{{ routes.account_addresses_delete_url }}?address={{ address.id }}" class="address-delete" data-confirm="{{ 'customer.addresses.delete_confirm' | t }}">{{ 'customer.addresses.delete' | t }}</a>
            </div>
          </li>
        {% endfor %}
      </ul>

      <a href="{{ routes.account_addresses_new_url }}" class="account-button">{{ 'customer.addresses.add_new' | t }}</a>

      {% if paginate.pages > 1 %}
        {% include 'pagination' %}
      {% endif %}
    {% endpaginate %}
  </div>
{% else %}
  <p class="account-empty">{{ 'customer.addresses.empty' | t }}</p>
  <a href="{{ routes.account_addresses_new_url }}" class="account-button">{{ 'customer.addresses.add_first' | t }}</a>
{% endif %}

{% layout 'page' %}

<header class="page-header">
  <h1>{{ page.title }}</h1>
</header>

<div class="contact-page-layout">
  <div class="contact-form-section">
    {% form 'contact' %}
      {% if form.posted_successfully? %}
        <div class="form-success" role="status">{{ 'sections.contact.success' | t }}</div>
      {% else %}
        <div class="field-group">
          <label for="ContactName">{{ 'sections.contact.name.label' | t }}</label>
          <input type="text" id="ContactName" name="contact[name]" value="{{ form.name }}" required>
        </div>
        <div class="field-group">
          <label for="ContactEmail">{{ 'sections.contact.email.label' | t }}</label>
          <input type="email" id="ContactEmail" name="contact[email]" value="{{ form.email }}" required>
        </div>
        <div class="field-group">
          <label for="ContactSubject">{{ 'sections.contact.subject.label' | t }}</label>
          <input type="text" id="ContactSubject" name="contact[subject]" value="{{ form.subject }}">
        </div>
        <div class="field-group">
          <label for="ContactMessage">{{ 'sections.contact.message.label' | t }}</label>
          <textarea id="ContactMessage" name="contact[body]" rows="5" required>{{ form.body }}</textarea>
        </div>
        {% if form.errors %}
          <div class="form-error" role="alert">{{ 'sections.contact.error' | t }}</div>
        {% endif %}
        <button type="submit" class="contact-submit">{{ 'sections.contact.submit' | t }}</button>
      {% endif %}
    {% endform %}
  </div>

  <div class="contact-info-section">
    {% if settings.store_address != blank %}
      <div class="contact-info-block">
        <h2>{{ 'sections.contact.info_title' | t }}</h2>
        <div class="contact-address">{{ settings.store_address }}</div>
      </div>
    {% endif %}

    {% if settings.store_phone != blank %}
      <p><strong>{{ 'sections.contact.phone' | t }}</strong> <a href="tel:{{ settings.store_phone | replace: ' ', '' }}">{{ settings.store_phone }}</a></p>
    {% endif %}

    {% if settings.store_email != blank %}
      <p><strong>{{ 'sections.contact.email_title' | t }}</strong> <a href="mailto:{{ settings.store_email }}">{{ settings.store_email }}</a></p>
    {% endif %}

    {% if settings.business_hours != blank %}
      <div class="contact-hours">
        <h3>{{ 'sections.contact.hours_title' | t }}</h3>
        {{ settings.business_hours }}
      </div>
    {% endif %}

    {% if settings.contact_map_url != blank %}
      <div class="contact-map">
        <iframe src="{{ settings.contact_map_url }}" title="{{ 'sections.contact.map_title' | t }}" allowfullscreen loading="lazy"></iframe>
      </div>
    {% endif %}
  </div>
</div>

{% comment %} Basic Rich Text Section {% endcomment %}
<section class="rich-text-section section-{{ section.id }}" aria-labelledby="richtext-heading-{{ section.id }}">
  <div class="container">
    {% if section.settings.heading != blank %}
      <h2 id="richtext-heading-{{ section.id }}">{{ section.settings.heading }}</h2>
    {% endif %}

    {% if section.settings.text != blank %}
      <div class="rte align-{{ section.settings.text_alignment }}">
        {{ section.settings.text }}
      </div>
    {% else %}
      <p class="empty-state rte">{{ 'sections.rich_text.no_content' | t }}</p>
    {% endif %}
  </div>
</section>

{% schema %}
{
  "name": "{{ 'sections.rich_text.name' | t }}",
  "tag": "section",
  "class": "section-rich-text",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "{{ 'sections.rich_text.heading' | t }}",
      "default": "{{ 'sections.rich_text.default_heading' | t }}"
    },
    {
      "type": "richtext",
      "id": "text",
      "label": "{{ 'sections.rich_text.content' | t }}",
      "default": "<p>{{ 'sections.rich_text.default_content' | t }}</p><p><a href=\"#\">{{ 'sections.rich_text.default_link' | t }}</a></p>"
    },
    {
      "type": "select",
      "id": "text_alignment",
      "label": "{{ 'sections.rich_text.alignment' | t }}",
      "options": [
        { "value": "left", "label": "{{ 'sections.rich_text.align_left' | t }}" },
        { "value": "center", "label": "{{ 'sections.rich_text.align_center' | t }}" },
        { "value": "right", "label": "{{ 'sections.rich_text.align_right' | t }}" }
      ],
      "default": "left"
    },
    {
      "type": "color",
