  "presets": [
    {
      "name": "{{ 'sections.rich_text.name' | t }}",
      "settings": {
        "text_alignment": "center"
      }
    }
  ],
  "enabled_on": {
    "templates": ["*"],
    "groups": ["custom"]
  }
}
