{% comment %} Basic Announcement Bar with Countdown {% endcomment %}
{% if section.settings.text != blank %}
  <div 
    class="announcement-bar"
    role="region"
    aria-live="polite"
    aria-label="{{ 'sections.announcement_bar.region_label' | t }}"
