{% comment %} Basic Custom Content Section {% endcomment %}
<section class="custom-content">
  {% if section.settings.heading != blank %}
    <h2>{{ section.settings.heading | escape }}</h2>
  {% endif %}
  {% if section.blocks.size > 0 %}
    <div class="content-blocks">
      {% for block in section.blocks %}
        {% case block.type %}
          {% when 'text' %}
            <div class="text-block">{{ block.settings.text }}</div>
          {% when 'image' %}
            {% if block.settings.image != blank %}
              <img src="{{ block.settings.image | image_url: width: 500 }}" alt="{{ block.settings.image.alt | escape }}" loading="lazy">
            {% else %}
              <p>{{ 'sections.custom_content.no_image' | t }}</p>
            {% endif %}
          {% when 'html' %}
            <div class="html-block">{{ block.settings.html }}</div>
        {% endcase %}
      {% endfor %}
    </div>
  {% else %}
    <p>{{ 'sections.custom_content.no_blocks' | t }}</p>
  {% endif %}
</section>

