{% comment %} Intermediate Price Display Snippet {% endcomment %}
{% assign price = include.price | default: product.price %}
{% assign compare_at = include.compare_at | default: product.compare_at_price %}
<div class="price-display">
  {% if price > 0 %}
    {% if compare_at > price %}
      <span class="compare-at">{{ compare_at | money }}</span>
    {% endif %}
    <span class="current-price">{{ price | money }}</span>
  {% else %}
    <span class="free">{{ 'snippets.price.free' | t }}</span>
  {% endif %}
  {% if include.unit_price %}
    <span class="unit-price">{{ include.unit_price | money }} / {{ include.unit_price_measurement }}</span>
  {% endif %}
  {% unless include.available %}
    <span class="sold-out">{{ 'snippets.price.sold_out' | t }}</span>
  {% endunless %}
</div>

{% comment %} Advanced 404 Template {% endcomment %}
<main class="page-404">
  <h1>{{ 'templates.404.title' | t }}</h1>
  <p>{{ 'templates.404.message' | t }}</p>
  <form action="/search" method="get">
    <input type="search" name="q" placeholder="{{ 'templates.404.search_placeholder' | t }}" aria-label="{{ 'templates.404.search_label' | t }}">
    <button type="submit">{{ 'templates.404.search' | t }}</button>
  </form>
  {% if settings.featured_collection != blank %}
    <section class="featured-products">
      <h2>{{ 'templates.404.featured' | t }}</h2>
      {% assign coll = collections[settings.featured_collection] %}
      {% if coll.products.size > 0 %}
        {% for prod in coll.products limit: 4 %}
          {% include 'product-card' with product: prod %}
        {% endfor %}
      {% else %}
        <p>{{ 'templates.404.no_products' | t }}</p>
      {% endif %}
    </section>
  {% endif %}
  <a href="/">{{ 'templates.404.home' | t }}</a>
</main>
{% schema %}
{
  "name": "404 Page"
}
{% endschema %}

{% if product.media.size > 0 %}
  <div class="product-media-gallery">
    <div class="media-primary">
      {% for media in product.media %}
        <div class="media-item {% if forloop.first %}media-item--active{% endif %}" data-media-id="{{ media.id }}">
          {% if media.media_type == 'image' %}
            {%- assign img_url = media.preview_image | image_url: width: 800 -%}
            <img src="{{ img_url }}&width=800"
                 srcset="{{ img_url }}&width=400 400w, {{ img_url }}&width=800 800w"
                 alt="{{ media.alt | escape }}"
                 loading="{% if forloop.first %}eager{% else %}lazy{% endif %}"
                 width="800"
                 height="800"
                 class="media-image">
          {% elsif media.media_type == 'video' %}
            {{ media | media_tag: image_size: '800x', controls: true, autoplay: false, muted: true }}
          {% endif %}
        </div>
      {% endfor %}
    </div>

    {% if product.media.size > 1 %}
      <div class="media-thumbnails">
        {% for media in product.media %}
          <button class="thumbnail {% if forloop.first %}thumbnail--active{% endif %}"
                  data-thumbnail-for="{{ media.id }}"
                  aria-label="{{ 'products.product.media_thumbnail_alt' | t: index: forloop.index }}">
            {% if media.media_type == 'image' %}
              {%- assign thumb_url = media.preview_image | image_url: width: 100 -%}
              <img src="{{ thumb_url }}&width=100" alt="" loading="lazy">
            {% else %}
              {% include 'icon' with 'play' %}
            {% endif %}
          </button>
        {% endfor %}
      </div>
    {% endif %}
  </div>
{% endif %}

{% comment %} Basic Testimonials Section {% endcomment %}
<section class="testimonials-section" aria-labelledby="testimonials-heading">
  <h2 id="testimonials-heading">{{ section.settings.heading | default: 'sections.testimonials.heading' | t }}</h2>
  {% if section.blocks.size > 0 %}
    <div class="testimonials-carousel" data-autoplay="{{ section.settings.autoplay }}">
      {% for block in section.blocks %}
        <div class="testimonial-slide">
          {% if block.settings.rating > 0 %}
            <div class="rating">
              {% for i in (1..block.settings.rating) %}
                {% render 'icon-star' %}
              {% endfor %}
            </div>
          {% endif %}
          <blockquote>{{ block.settings.quote | default: 'sections.testimonials.default_quote' | t }}</blockquote>
          {% if block.settings.image != blank %}
            <img src="{{ block.settings.image | image_url: width: 80 }}" alt="{{ block.settings.name | escape }}" loading="lazy">
          {% else %}
            <div class="placeholder-avatar">{{ 'sections.testimonials.placeholder' | t }}</div>
          {% endif %}
          <cite>{{ block.settings.name | default: 'sections.testimonials.default_name' | t }}</cite>
        </div>
      {% endfor %}
      <button class="prev" aria-label="{{ 'sections.testimonials.previous' | t }}">Prev</button>
      <button class="next" aria-label="{{ 'sections.testimonials.next' | t }}">Next</button>
    </div>
  {% else %}
    <p class="empty-state">{{ 'sections.testimonials.no_testimonials' | t }}</p>
  {% endif %}
</section>

