      "default": "{{ 'sections.testimonials.default_heading' | t }}"
    },
    {
      "type": "checkbox",
      "id": "autoplay",
      "label": "{{ 'sections.testimonials.autoplay' | t }}",
      "default": true
    }
  ],
  "blocks": [
    {
      "type": "testimonial",
      "name": "{{ 'sections.testimonials.block' | t }}",
      "limit": 5,
      "settings": [
        {
          "type": "textarea",
          "id": "quote",
          "label": "{{ 'sections.testimonials.quote' | t }}"
        },
        {
          "type": "text",
          "id": "name",
          "label": "{{ 'sections.testimonials.name' | t }}"
        },
        {
          "type": "image_picker",
          "id": "image",
          "label": "{{ 'sections.testimonials.image' | t }}"
        },
        {
          "type": "range",
          "id": "rating",
          "min": 1,
          "max": 5,
          "step": 1,
          "label": "{{ 'sections.testimonials.rating' | t }}",
          "default": 5
        }
      ]
    }
  ],
  "presets": [
    {
      "name": "{{ 'sections.testimonials.name' | t }}",
      "blocks": [
        {"type": "testimonial"}
      ]
    }
  ]
}
{% endschema %}

{% comment %} Advanced Blog Template {% endcomment %}
{% paginate blog.articles by 10 %}
  <header>
    <h1>{{ blog.title }}</h1>
    <a href="{{ blog.url }}/atom.xml" aria-label="{{ 'templates.blog.rss' | t }}">RSS</a>
  </header>
  <div class="blog-layout">
    <main>
      {% if blog.articles.size > 0 %}
        {% for article in blog.articles %}
          <article>
            {% if article.image %}
              <img src="{{ article.image | image_url: width: 300 }}" alt="{{ article.title | escape }}" loading="lazy">
            {% endif %}
            <h2><a href="{{ article.url }}">{{ article.title }}</a></h2>
            <p class="meta">{{ article.published_at | date: format: 'date' }} - {{ article.author }}</p>
            <p>{{ article.excerpt_or_content | strip_html | truncate: 150 }}</p>
            <a href="{{ article.url }}">{{ 'templates.blog.read_more' | t }}</a>
          </article>
        {% endfor %}
      {% else %}
        <p>{{ 'templates.blog.no_articles' | t }}</p>
      {% endif %}
      {% include 'pagination' %}
    </main>
    <aside class="sidebar">
      {% if blog.all_tags.size > 0 %}
        <div class="tags">
          <h3>{{ 'templates.blog.tags' | t }}</h3>
          <ul>
            {% for tag in blog.all_tags %}
              <li><a href="{{ blog.url }}/tagged/{{ tag | handle }}">{{ tag }}</a></li>
            {% endfor %}
          </ul>
        </div>
      {% endif %}
      <div class="recent-articles">
        <h3>{{ 'templates.blog.recent' | t }}</h3>
        <ul>
          {% for recent in blog.articles limit: 5 %}
            <li><a href="{{ recent.url }}">{{ recent.title | truncate: 30 }}</a></li>
          {% endfor %}
        </ul>
      </div>
    </aside>
  </div>
{% endpaginate %}
{% schema %}
{
  "name": "Blog"
}
{% endschema %}

{% comment %} Basic Product Grid Section {% endcomment %}
