    },
    {
      "type": "range",
      "id": "limit",
      "min": 4,
      "max": 12,
      "label": "{{ 'sections.product_grid.limit' | t }}",
      "default": 8
    }
  ],
  "presets": [
    {
      "name": "{{ 'sections.product_grid.name' | t }}"
    }
  ]
}
{% endschema %}

{% comment %} Advanced Article Template {% endcomment %}
<article class="article">
  {% if article.image %}
    <img src="{{ article.image | image_url: width: 1200 }}" alt="{{ article.title | escape }}" loading="eager">
  {% endif %}
  <h1>{{ article.title }}</h1>
  <div class="meta">
    <p>{{ article.published_at | date: format: 'date' }} by {{ article.author }}</p>
  </div>
  <div class="content">{{ article.content }}</div>
  {% if article.author_bio %}
    <aside class="author-bio">
      <h2>{{ 'templates.article.author' | t }}</h2>
      <p>{{ article.author_bio }}</p>
    </aside>
  {% endif %}
  {% include 'social-share' %}
  {% if blog.comments_enabled? %}
    <div class="comments">
      <h2>{{ 'templates.article.comments' | t }}</h2>
      {% for comment in article.comments %}
        <div class="comment">
          <p>{{ comment.content }}</p>
          <p>By {{ comment.author }} on {{ comment.created_at | date: format: 'date' }}</p>
        </div>
      {% endfor %}
      {% form 'new_comment', article %}
        <!-- Comment form -->
      {% endform %}
    </div>
  {% endif %}
  {% if blog.articles_with_tag.size > 1 %}
    <section class="related-articles">
      <h2>{{ 'templates.article.related' | t }}</h2>
      {% for rel in blog.articles_with_tag limit: 3 %}
        {% unless rel.handle == article.handle %}
          <a href="{{ rel.url }}">{{ rel.title }}</a>
        {% endunless %}
      {% endfor %}
    </section>
  {% endif %}
</article>
{% schema %}
{
  "name": "Article"
}
{% endschema %}

{% comment %} Basic Hero / Banner Section {% endcomment %}
<section class="hero-section" aria-labelledby="hero-heading">
  {% if section.settings.background_image != blank %}
    {% assign img = section.settings.background_image | image_url: width: 1800 %}
    <div class="hero-background"
         style="background-image: url('{{ img }}');">
      <div class="hero-overlay" style="--overlay-opacity: {{ section.settings.overlay_opacity | divided_by: 100.0 }};">
        <div class="hero-content">
          {% if section.settings.heading != blank %}
            <h1 id="hero-heading">{{ section.settings.heading }}</h1>
          {% endif %}
          {% if section.settings.subheading != blank %}
            <p class="subheading">{{ section.settings.subheading }}</p>
          {% endif %}
          {% if section.settings.button_label != blank and section.settings.button_link != blank %}
            <a href="{{ section.settings.button_link }}"
               class="hero-button"
               aria-label="{{ section.settings.button_label | escape }}">
              {{ section.settings.button_label }}
            </a>
          {% endif %}
        </div>
      </div>
    </div>
  {% else %}
    <div class="hero-fallback" style="background-color: {{ section.settings.fallback_color | default: '#f5f5f5' }};">
      <div class="hero-content">
        <h1 id="hero-heading">{{ section.settings.heading | default: 'sections.hero.default_heading' | t }}</h1>
        <p>{{ section.settings.subheading | default: 'sections.hero.default_subheading' | t }}</p>
      </div>
    </div>
  {% endif %}
</section>

{% schema %}
