{% comment %} Intermediate Collection Card Snippet {% endcomment %}
{% assign coll = include.collection %}
{% if coll != blank %}
  <div class="collection-card">
    <a href="{{ coll.url }}" class="card-link" aria-label="{{ 'snippets.collection_card.view_collection' | t: title: coll.title | escape }}">
      {% if coll.image != blank %}
        {% assign img = coll.image | image_url: width: 600 %}
        <img src="{{ img }}"
             srcset="{{ coll.image | image_url: width: 300 }} 300w, {{ img }} 600w"
             sizes="(max-width: 750px) 100vw, 300px"
             alt="{{ coll.image.alt | default: coll.title | escape }}"
             width="600"
             height="{{ coll.image.height | times: 600 | divided_by: coll.image.width }}"
             loading="lazy">
      {% else %}
        <div class="collection-placeholder">{{ 'snippets.collection_card.no_image' | t }}</div>
      {% endif %}

      <div class="card-overlay">
        <h3 class="collection-title">{{ coll.title | escape }}</h3>
        {% if coll.all_products_count > 0 %}
          <span class="product-count-badge">
            {{ coll.all_products_count }} {{ 'snippets.collection_card.products' | t }}
          </span>
        {% else %}
          <span class="product-count-badge empty">{{ 'snippets.collection_card.empty' | t }}</span>
        {% endif %}
      </div>
    </a>

    {% if coll.description != blank %}
      <p class="collection-description">
        {{ coll.description | strip_html | truncate: 80 }}
      </p>
    {% endif %}
  </div>
{% else %}
  <p class="empty-state">{{ 'snippets.collection_card.no_collection' | t }}</p>
{% endif %}

{% comment %} Basic Newsletter Signup Section {% endcomment %}
<section class="newsletter-section" aria-labelledby="newsletter-heading">
