{% comment %} Intermediate Variant Selector Snippet {% endcomment %}
{% assign current_variant = product.selected_or_first_available_variant %}
<div class="variant-selector" data-product-id="{{ product.id }}" role="group" aria-label="{{ 'snippets.variant_selector.options' | t }}">
  {% for option in product.options_with_values %}
    <fieldset class="variant-option">
      <legend>{{ option.name }}</legend>

      {% if option.name contains 'Color' or option.name contains 'colour' %}
        <div class="swatch-group" role="radiogroup">
          {% for value in option.values %}
            {% assign variant = product.variants | where: option.name, value | first %}
            <label 
              class="swatch {% if current_variant == variant %}selected{% endif %}" 
              for="option-{{ option.name | handle }}-{{ forloop.index }}"
            >
              <input 
                type="radio"
                id="option-{{ option.name | handle }}-{{ forloop.index }}"
                name="{{ option.name | handle }}"
                value="{{ value | escape }}"
                {% if current_variant == variant %}checked{% endif %}
                {% unless variant.available %}disabled{% endunless %}
                aria-label="{{ value }} {% unless variant.available %}{{ 'snippets.variant_selector.sold_out' | t }}{% endunless %}"
                data-variant-id="{{ variant.id }}"
                data-price="{{ variant.price }}"
              >
              <span class="swatch-color" style="background-color: {{ value | downcase }};"></span>
            </label>
          {% endfor %}
        </div>
      {% else %}
        <select name="{{ option.name | handle }}" aria-label="{{ option.name }}">
          {% for value in option.values %}
            {% assign variant = product.variants | where: option.name, value | first %}
            <option 
              value="{{ value | escape }}" 
              {% if current_variant == variant %}selected{% endif %}
              {% unless variant.available %}disabled{% endunless %}
              data-variant-id="{{ variant.id }}"
              data-price="{{ variant.price }}"
            >
              {{ value }}{% unless variant.available %} – {{ 'snippets.variant_selector.sold_out' | t }}{% endunless %}
            </option>
          {% endfor %}
        </select>
      {% endif %}
    </fieldset>
  {% endfor %}

  <p class="availability-message" aria-live="polite">
    {% if current_variant.available %}
      {{ 'snippets.variant_selector.in_stock' | t }}
    {% else %}
      {{ 'snippets.variant_selector.out_of_stock' | t }}
    {% endif %}
  </p>
</div>

{% if section.blocks.size > 0 %}
  <section class="faq-collapsible" aria-label="{{ 'sections.faq_collapsible.aria_label' | t }}">
    {% for block in section.blocks %}
      <div class="faq-item" {{ block.shopify_attributes }}>
        <h3 class="faq-question">
          <button class="faq-toggle"
                  aria-expanded="{% if forloop.first %}true{% else %}false{% endif %}"
                  aria-controls="faq-answer-{{ block.id }}"
                  id="faq-question-{{ block.id }}">
            {{ block.settings.question | t }}
            <span class="faq-toggle-icon" aria-hidden="true">{% if forloop.first %}−{% else %}+{% endif %}</span>
          </button>
        </h3>
        <div id="faq-answer-{{ block.id }}"
             class="faq-answer"
             aria-labelledby="faq-question-{{ block.id }}"
             {% unless forloop.first %}hidden{% endunless %}>
          {{ block.settings.answer | t }}
        </div>
      </div>
    {% endfor %}
  </section>
{% else %}
  <p class="faq-empty">{{ 'sections.faq_collapsible.empty' | t }}</p>
{% endif %}

{% schema %}
{
  "name": "{{'sections.faq_collapsible.name' | t}}",
  "tag": "section",
  "class": "faq-collapsible-section",
  "max_blocks": 10,
  "blocks": [
    {
      "type": "faq_item",
      "name": "{{'sections.faq_collapsible.item_name' | t}}",
      "settings": [
        {
          "type": "text",
          "id": "question",
          "label": "{{'sections.faq_collapsible.question.label' | t}}",
          "default": "{{'sections.faq_collapsible.question.default' | t}}"
        },
        {
          "type": "richtext",
          "id": "answer",
          "label": "{{'sections.faq_collapsible.answer.label' | t}}",
          "default": "{{'sections.faq_collapsible.answer.default' | t}}"
        }
      ]
    }
  ],
  "presets": [
    {
      "name": "{{'sections.faq_collapsible.preset.name' | t}}",
      "category": "{{'sections.faq_collapsible.preset.category' | t}}",
      "blocks": [
        { "type": "faq_item" },
        { "type": "faq_item" },
        { "type": "faq_item" }
