{% if product_recommendations.performed and product_recommendations.products.size > 0 %}
  <section class="product-recommendations">
    <h2 class="recommendations-title">{{ 'products.product.recommended_for_you' | t }}</h2>
    <div class="product-grid">
      {% for product in product_recommendations.products limit: 4 %}
