{% assign free_threshold = shop.metafields.shipping.free_threshold.value %}
{% assign cart_amount = cart.total_price %}

{% if free_threshold and cart_amount < free_threshold %}
  {% assign amount_needed = free_threshold | minus: cart_amount %}
  <div class="free-shipping-bar" role="status" aria-live="polite">
    {{ 'cart.general.free_shipping_progress' | t: amount: amount_needed | money }}
  </div>
{% endif %}

