<a href="javascript:history.back()" class="policy-back-link">{{ 'pages.policy.back' | t }}</a>

{% comment %}
  Hero Slideshow Section
  Dependencies: None
{% endcomment %}

{{ 'section-slideshow.css' | asset_url | stylesheet_tag }}

{%- liquid
  assign should_autoplay = section.settings.autoplay
  assign autoplay_delay = section.settings.autoplay_delay | times: 1000
  assign image_ratio = section.settings.image_ratio
  assign text_alignment = section.settings.text_alignment
  assign show_text_overlay = section.settings.show_text_overlay
-%}

<slide-show-component
  class="slideshow section-{{ section.id }} color-{{ section.settings.color_scheme }}"
  data-autoplay="{{ should_autoplay }}"
  data-delay="{{ autoplay_delay }}"
  role="region"
  aria-roledescription="carousel"
  aria-label="{{ 'sections.slideshow.label' | t }}"
>
  <div class="slideshow__viewport" id="Slideshow-{{ section.id }}">
    <div class="slideshow__track">
      {%- for block in section.blocks -%}
        {%- liquid
          assign image = block.settings.image
          assign mobile_image = block.settings.mobile_image
          assign heading = block.settings.heading
          assign subheading = block.settings.subheading
          assign button_label = block.settings.button_label
          assign button_link = block.settings.button_link
        -%}

        <div
          class="slideshow__slide slideshow__slide--{{ forloop.index }} {% if forloop.first %}is-active{% endif %}"
          {{ block.shopify_attributes }}
          role="group"
          aria-roledescription="slide"
          aria-label="{{ forloop.index }} {{ 'general.of' | t }} {{ forloop.length }}"
          data-index="{{ forloop.index0 }}"
        >
          <div class="slideshow__media">
            {%- if image -%}
              <picture>
                {%- if mobile_image -%}
                  <source
                    media="(max-width: 749px)"
                    srcset="{{ mobile_image | image_url: width: 750, height: 1000 }}"
                  >
                {%- endif -%}
                <img
                  srcset="
                    {{ image | image_url: width: 1200 }} 1200w,
                    {{ image | image_url: width: 1600 }} 1600w,
                    {{ image | image_url: width: 2000 }} 2000w
                  "
                  sizes="100vw"
                  alt="{{ image.alt | escape }}"
                  loading="lazy"
                  width="{{ image.width }}"
                  height="{{ image.height }}"
                  class="slideshow__image"
                >
              </picture>
            {%- else -%}
              <div class="slideshow__placeholder placeholder-background">
                {{ 'lifestyle-1' | placeholder_svg_tag: 'placeholder-svg' }}
              </div>
            {%- endif -%}
          </div>

          {%- if heading != blank or subheading != blank or button_label != blank -%}
            <div class="slideshow__content {{ text_alignment }} {% unless show_text_overlay %}slideshow__content--no-overlay{% endunless %}">
              <div class="slideshow__content-inner page-width">
                {%- if heading != blank -%}
                  <h2 class="slideshow__heading h1">
                    {{ heading | escape }}
                  </h2>
                {%- endif -%}
                {%- if subheading != blank -%}
                  <div class="slideshow__subheading rte">
                    {{ subheading }}
                  </div>
                {%- endif -%}
                {%- if button_label != blank and button_link != blank -%}
                  <div class="slideshow__buttons">
                    <a href="{{ button_link }}" class="button button--primary">
                      {{ button_label | escape }}
                    </a>
                  </div>
                {%- endif -%}
              </div>
            </div>
          {%- endif -%}
        </div>
      {%- else -%}
        <div class="slideshow__slide">
          <div class="slideshow__placeholder placeholder-background">
            {{ 'lifestyle-2' | placeholder_svg_tag: 'placeholder-svg' }}
          </div>
          <div class="slideshow__content center">
            <h2 class="slideshow__heading h1">{{ 'sections.slideshow.onboarding_title' | t }}</h2>
          </div>
        </div>
      {%- endfor -%}
    </div>
  </div>

  {%- if section.blocks.size > 1 -%}
    <div class="slideshow__controls no-js-hidden">
      <button
        type="button"
        class="slideshow__button slideshow__button--prev button--is-secondary"
        name="previous"
        aria-label="{{ 'sections.slideshow.previous_slide' | t }}"
        aria-controls="Slideshow-{{ section.id }}"
      >
        {% render 'icon-caret' %}
      </button>

      <div class="slideshow__dots-wrapper" aria-hidden="true">
        <div class="slideshow__dots">
          {%- for block in section.blocks -%}
            <button
              type="button"
              class="slideshow__dot {% if forloop.first %}is-active{% endif %}"
              aria-label="{{ 'sections.slideshow.load_slide' | t }} {{ forloop.index }}"
              aria-controls="Slideshow-{{ section.id }}"
              data-index="{{ forloop.index0 }}"
            >
              <span class="visually-hidden">Slide {{ forloop.index }}</span>
            </button>
          {%- endfor -%}
        </div>
      </div>

      <button
        type="button"
        class="slideshow__button slideshow__button--next button--is-secondary"
        name="next"
        aria-label="{{ 'sections.slideshow.next_slide' | t }}"
        aria-controls="Slideshow-{{ section.id }}"
      >
        {% render 'icon-caret' %}
      </button>

      {%- if should_autoplay -%}
        <button
          type="button"
          class="slideshow__button slideshow__button--pause button--is-secondary"
          name="pause"
          aria-label="{{ 'sections.slideshow.pause_slideshow' | t }}"
          data-pause
        >
          {% render 'icon-pause' %}
          {% render 'icon-play' %}
        </button>
      {%- endif -%}
    </div>
  {%- endif -%}
</slide-show-component>

{% schema %}
{
  "name": "{{ 'sections.slideshow.name' | t }}",
  "tag": "section",
  "class": "section-slideshow",
  "settings": [
    {
      "type": "select",
      "id": "image_ratio",
      "options": [
        {
          "value": "1100",
          "label": "{{ 'sections.slideshow.option_1100' | t }}"
        },
        {
          "value": "1300",
          "label": "{{ 'sections.slideshow.option_1300' | t }}"
        },
        {
          "value": "1500",
          "label": "{{ 'sections.slideshow.option_1500' | t }}"
        }
      ],
      "default": "1100",
      "label": "{{ 'sections.slideshow.settings.image_ratio' | t }}"
    },
    {
