{% comment %}
  Testimonials Section
  Dependencies: icon-star
{% endcomment %}

{{ 'component-rating.css' | asset_url | stylesheet_tag }}
{{ 'section-testimonials.css' | asset_url | stylesheet_tag }}

<div class="section-{{ section.id }} color-{{ section.settings.color_scheme }}">
  <div class="page-width">
    <div class="title-wrapper center {% if section.settings.heading == blank %}title-wrapper--no-heading{% endif %}">
      {%- if section.settings.subheading != blank -%}
        <h2 class="testimonials__subheading subtitle">{{ section.settings.subheading | escape }}</h2>
      {%- endif -%}
      {%- if section.settings.heading != blank -%}
        <h2 class="title {{ section.settings.heading_size }}">{{ section.settings.heading | escape }}</h2>
      {%- endif -%}
    </div>

    {%- if section.blocks.size > 0 -%}
      <slider-component class="slider-mobile-gutter">
        <ul class="testimonials-list contains-content-container grid grid--1-col{% if section.blocks.size >= 3 %} grid--3-col-tablet grid--3-col-desktop{% else %} grid--{{ section.blocks.size }}-col-tablet{% endif %}"
            id="Slider-{{ section.id }}"
            role="list"
        >
          {%- for block in section.blocks -%}
            <li class="testimonials__item grid__item" {{ block.shopify_attributes }}>
              <div class="testimonials__content">
                <div class="testimonial-header">
                  {%- if block.settings.rating > 0 -%}
                    <div class="rating" role="img" aria-label="{{ 'accessibility.star_reviews_info' | t: rating: block.settings.rating }}">
                      {%- assign rating_decimal = 0 -%}
                      {%- if block.settings.rating == 3.5 or block.settings.rating == 2.5 or block.settings.rating == 1.5 -%}
                        {%- assign rating_decimal = 0.5 -%}
                      {%- elsif block.settings.rating == 4.5 -%}
                        {%- assign rating_decimal = 0.5 -%}
                      {%- endif -%}

                      <span aria-hidden="true" class="rating-star color-icon-{{ settings.accent_icons }}" style="--rating: {{ block.settings.rating }}; --rating-max: 5; --rating-decimal: {{ rating_decimal }};">&nbsp;</span>
                    </div>
                  {%- endif -%}
                  {%- if block.settings.author != blank -%}
                    <span class="testimonial-author h3">{{ block.settings.author | escape }}</span>
                  {%- else -%}
                    <span class="testimonial-author h3">{{ 'sections.testimonials.default_author' | t }}</span>
                  {%- endif -%}
                </div>

                {%- if block.settings.text != blank -%}
                  <div class="testimonial-text rte">
                    <blockquote>{{ block.settings.text }}</blockquote>
                  </div>
                {%- else -%}
                  <div class="testimonial-text rte">
                    <blockquote>{{ 'sections.testimonials.default_text' | t }}</blockquote>
                  </div>
                {%- endif -%}

                {%- if block.settings.image != blank -%}
                  <div class="testimonial-avatar">
                    <img
                      src="{{ block.settings.image | image_url: width: 100 }}"
                      alt="{{ block.settings.author | escape }}"
                      loading="lazy"
                      width="50"
                      height="50"
                      class="image--circle"
                    >
                  </div>
                {%- endif -%}
              </div>
            </li>
          {%- endfor -%}
        </ul>
      </slider-component>
    {%- else -%}
      <div class="testimonials-placeholder placeholder-wrapper">
        <div class="placeholder-content">
          {{ 'section-testimonials' | placeholder_svg_tag: 'placeholder-svg' }}
          <p>{{ 'sections.testimonials.no_content' | t }}</p>
        </div>
      </div>
    {%- endif -%}
  </div>
</div>

