{{ 'section-newsletter.css' | asset_url | stylesheet_tag }}

<div class="newsletter section-{{ section.id }} color-{{ section.settings.color_scheme }}">
  <div class="newsletter__wrapper page-width">
    <div class="newsletter__content">
      {%- for block in section.blocks -%}
        <div class="newsletter__block {{ block.type }}" {{ block.shopify_attributes }}>
          {%- case block.type -%}
            {%- when '@app' -%}
              {% render block %}
            {%- when 'heading' -%}
              <h2 class="{{ block.settings.heading_size }}">{{ block.settings.heading | escape }}</h2>
            {%- when 'paragraph' -%}
              <div class="newsletter__subheading rte">{{ block.settings.text }}</div>
            {%- when 'email_form' -%}
              <div class="newsletter-form">
                {% form 'customer', class: 'newsletter-form__form' %}
                  <input type="hidden" name="contact[tags]" value="newsletter">
                  <div class="newsletter-form__field-wrapper">
                    <div class="field">
                      <input
                        id="NewsletterForm--{{ section.id }}"
                        type="email"
                        name="contact[email]"
                        class="field__input"
                        value="{{ form.email }}"
                        aria-required="true"
                        autocorrect="off"
                        autocapitalize="off"
                        autocomplete="email"
                        {% if form.errors %}
                          autofocus
                          aria-invalid="true"
                          aria-describedby="Newsletter-error--{{ section.id }}"
                        {% elsif form.posted_successfully? %}
                          aria-describedby="Newsletter-success--{{ section.id }}"
                        {% endif %}
                        placeholder="{{ 'newsletter.label' | t }}"
                        required
                      >
                      <label class="field__label" for="NewsletterForm--{{ section.id }}">
                        {{ 'newsletter.label' | t }}
                      </label>
                      <button type="submit" class="newsletter-form__button field__button" name="commit" id="Subscribe" aria-label="{{ 'newsletter.button_label' | t }}">
                        {% render 'icon-arrow' %}
                      </button>
                    </div>
                    {%- if form.errors -%}
                      <small class="newsletter-form__message form__message" id="Newsletter-error--{{ section.id }}">
                        {% render 'icon-error' %}{{ form.errors.translated_fields['email'] | capitalize }} {{ form.errors.messages['email'] }}
                      </small>
                    {%- endif -%}
                    {%- if form.posted_successfully? -%}
                      <h3 class="newsletter-form__message newsletter-form__message--success form__message" id="Newsletter-success--{{ section.id }}" tabindex="-1" autofocus>
                        {% render 'icon-success' %}{{ 'newsletter.success' | t }}
                      </h3>
                    {%- endif -%}
                  </div>
                {% endform %}
              </div>
          {%- endcase -%}
        </div>
      {%- endfor -%}
    </div>
  </div>
</div>

