{% schema %}
{
  "name": "Contact Form",
  "tag": "section",
  "class": "contact-section",
  "settings": [
    {
      "type": "text",
      "id": "heading",
      "label": "Section heading",
      "default": "Get in Touch"
    },
    {
      "type": "textarea",
      "id": "description",
      "label": "Section description",
      "default": "We'd love to hear from you. Send us a message and we'll respond as soon as possible."
    },
    {
      "type": "text",
      "id": "form_heading",
      "label": "Form heading",
      "default": "Send us a message"
    },
    {
      "type": "text",
      "id": "success_message",
      "label": "Success message",
      "default": "Thank you for your message. We'll get back to you soon!"
    },
    {
      "type": "checkbox",
      "id": "show_map",
      "label": "Show map",
      "default": true
    },
    {
      "type": "text",
      "id": "map_address",
      "label": "Map address",
      "default": "123 Business Street, City, State 12345"
    },
    {
      "type": "checkbox",
      "id": "show_contact_info",
      "label": "Show contact information",
      "default": true
    },
    {
      "type": "text",
      "id": "phone",
      "label": "Phone number",
      "default": "+1 (555) 123-4567"
    },
    {
      "type": "email",
      "id": "email",
      "label": "Email address",
      "default": "hello@example.com"
    },
    {
      "type": "text",
      "id": "hours",
      "label": "Business hours",
      "default": "Mon-Fri: 9AM-6PM, Sat: 10AM-4PM"
    }
  ],
  "presets": [
    {
      "name": "Contact Form"
    }
  ]
}
{% endschema %}

<div class="contact">
  <div class="contact__container">
    {% if section.settings.heading != blank %}
      <h2 class="contact__heading">{{ section.settings.heading | escape }}</h2>
    {% endif %}

    {% if section.settings.description != blank %}
      <p class="contact__description">{{ section.settings.description | escape }}</p>
    {% endif %}

    <div class="contact__layout">
      <div class="contact__form-wrapper">
        {% if section.settings.form_heading != blank %}
          <h3 class="contact__form-heading">{{ section.settings.form_heading | escape }}</h3>
        {% endif %}

        <form action="/contact#contact-form" method="post" class="contact__form" data-contact-form>
          <input type="hidden" name="form_type" value="contact">
          <input type="hidden" name="utf8" value="✓">

          <div class="contact__form-group">
            <label for="contact-name" class="contact__label">Name *</label>
            <input type="text"
                   id="contact-name"
                   name="contact[name]"
                   class="contact__input"
                   required
                   aria-required="true">
          </div>

          <div class="contact__form-group">
            <label for="contact-email" class="contact__label">Email *</label>
            <input type="email"
                   id="contact-email"
                   name="contact[email]"
                   class="contact__input"
                   required
                   aria-required="true">
          </div>

          <div class="contact__form-group">
            <label for="contact-phone" class="contact__label">Phone (optional)</label>
            <input type="tel"
                   id="contact-phone"
                   name="contact[phone]"
                   class="contact__input">
          </div>

          <div class="contact__form-group">
            <label for="contact-subject" class="contact__label">Subject</label>
            <input type="text"
                   id="contact-subject"
                   name="contact[subject]"
                   class="contact__input">
          </div>

          <div class="contact__form-group">
            <label for="contact-message" class="contact__label">Message *</label>
            <textarea id="contact-message"
                      name="contact[body]"
                      class="contact__textarea"
                      rows="5"
                      required
                      aria-required="true"></textarea>
          </div>

          <div class="contact__form-group contact__form-group--submit">
            <button type="submit" class="contact__submit button button--large">
              Send Message
            </button>
          </div>

          <div class="contact__message contact__message--success hidden" data-success-message>
            {{ section.settings.success_message | escape }}
          </div>

          <div class="contact__message contact__message--error hidden" data-error-message></div>
        </form>
      </div>

      <div class="contact__sidebar">
        {% if section.settings.show_map %}
          <div class="contact__map">
            <div class="contact__map-embed" data-map-embed>
              <iframe
                width="100%"
                height="300"
                frameborder="0"
                style="border:0"
                src="https://www.google.com/maps/embed/v1/place?key=YOUR_API_KEY&q={{ section.settings.map_address | url_encode }}&zoom=15"
                allowfullscreen>
              </iframe>
            </div>
            <div class="contact__map-address">
              {{ section.settings.map_address | escape }}
            </div>
          </div>
        {% endif %}

        {% if section.settings.show_contact_info %}
          <div class="contact__info">
            {% if section.settings.phone != blank %}
              <div class="contact__info-item">
                <h4 class="contact__info-title">Phone</h4>
                <a href="tel:{{ section.settings.phone }}" class="contact__info-link">
                  {{ section.settings.phone | escape }}
                </a>
              </div>
            {% endif %}

            {% if section.settings.email != blank %}
              <div class="contact__info-item">
                <h4 class="contact__info-title">Email</h4>
                <a href="mailto:{{ section.settings.email }}" class="contact__info-link">
                  {{ section.settings.email | escape }}
                </a>
              </div>
            {% endif %}

            {% if section.settings.hours != blank %}
              <div class="contact__info-item">
                <h4 class="contact__info-title">Business Hours</h4>
                <p class="contact__info-text">{{ section.settings.hours | escape }}</p>
              </div>
            {% endif %}
          </div>
        {% endif %}
      </div>
    </div>
  </div>
</div>

<style>
.contact {
  padding: 4rem 1rem;
  background: #f8f8f8;
}

.contact__container {
  max-width: 1200px;
  margin: 0 auto;
}

.contact__heading {
