  padding: 0.75rem;
  border: 1px solid #ddd;
  border-radius: 4px;
  font-size: 1rem;
  font-family: inherit;
}

.contact__input:focus,
.contact__textarea:focus {
  outline: none;
  border-color: #333;
}

.contact__textarea {
